import express from 'express';
import { authenticateToken } from '../middleware/auth';
import { stripePaymentService } from '../services/StripePaymentService';
import { pool } from '../config/duckdb-pool';

const router = express.Router();

// Middleware to parse raw body for webhooks
const rawBodyMiddleware = express.raw({ type: 'application/json' });

/**
 * Create payment intent for DRO investment
 */
router.post('/create-payment-intent', authenticateToken, async (req, res) => {
  try {
    const { droId, amount, currency = 'USD' } = req.body;
    const userId = (req as any).user.id;

    // Validate input
    if (!droId || !amount || amount <= 0) {
      return res.status(400).json({ 
        error: 'Valid DRO ID and amount are required' 
      });
    }

    // Verify DRO exists and is active
    const droResult = await pool.query(
      'SELECT id, title, minimum_investment, target_amount, raised_amount, status FROM dros WHERE id = ?',
      [droId]
    );

    if (droResult.rows.length === 0) {
      return res.status(404).json({ 
        error: 'DRO not found' 
      });
    }

    const dro = droResult.rows[0];

    if (dro.status !== 'active') {
      return res.status(400).json({ 
        error: 'DRO is not available for investment' 
      });
    }

    if (amount < dro.minimum_investment) {
      return res.status(400).json({ 
        error: `Minimum investment is ${dro.minimum_investment}` 
      });
    }

    // Convert to cents for Stripe
    const amountInCents = Math.round(amount * 100);

    // Create payment intent
    const paymentIntent = await stripePaymentService.createPaymentIntent({
      amount: amountInCents,
      currency,
      userId,
      droId,
      description: `Investment in ${dro.title}`,
      metadata: {
        droTitle: dro.title,
        investmentAmount: amount.toString(),
      }
    });

    // Calculate fees
    const fees = stripePaymentService.calculateFees(amountInCents);

    // Create pending investment record
    await pool.query(
      `INSERT INTO investments (investor_id, dro_id, amount, ownership_percentage, 
       status, payment_method, transaction_id) 
       VALUES (?, ?, ?, ?, 'pending', 'stripe', ?)`,
      [
        userId, 
        droId, 
        amount,
        (amount / dro.target_amount) * 100, // Calculate ownership percentage
        paymentIntent.id
      ]
    );

    res.json({
      paymentIntent: {
        id: paymentIntent.id,
        client_secret: paymentIntent.client_secret,
        amount: paymentIntent.amount,
        currency: paymentIntent.currency,
      },
      fees: {
        platformFee: fees.platformFee / 100, // Convert back to dollars
        stripeFee: fees.stripeFee / 100,
        netAmount: fees.netAmount / 100,
      },
      dro: {
        id: dro.id,
        title: dro.title,
        minimumInvestment: dro.minimum_investment,
      }
    });

  } catch (error) {
    console.error('Payment intent creation error:', error);
    res.status(500).json({ 
      error: 'Failed to create payment intent',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Confirm payment intent
 */
router.post('/confirm-payment', authenticateToken, async (req, res) => {
  try {
    const { paymentIntentId, paymentMethodId } = req.body;
    const userId = (req as any).user.id;

    if (!paymentIntentId || !paymentMethodId) {
      return res.status(400).json({ 
        error: 'Payment intent ID and payment method ID are required' 
      });
    }

    // Verify the payment intent belongs to this user
    const investmentResult = await pool.query(
      'SELECT id, dro_id, amount FROM investments WHERE transaction_id = ? AND investor_id = ?',
      [paymentIntentId, userId]
    );

    if (investmentResult.rows.length === 0) {
      return res.status(404).json({ 
        error: 'Investment not found or unauthorized' 
      });
    }

    // Confirm payment with Stripe
    const confirmedPayment = await stripePaymentService.confirmPaymentIntent(
      paymentIntentId, 
      paymentMethodId
    );

    res.json({
      paymentIntent: confirmedPayment,
      investment: investmentResult.rows[0]
    });

  } catch (error) {
    console.error('Payment confirmation error:', error);
    res.status(500).json({ 
      error: 'Failed to confirm payment',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Create withdrawal request
 */
router.post('/create-withdrawal', authenticateToken, async (req, res) => {
  try {
    const { amount, currency = 'USD', paymentMethodId } = req.body;
    const userId = (req as any).user.id;

    // Validate input
    if (!amount || amount <= 0 || !paymentMethodId) {
      return res.status(400).json({ 
        error: 'Valid amount and payment method are required' 
      });
    }

    // Check user's available balance (sum of payouts)
    const balanceResult = await pool.query(
      `SELECT COALESCE(SUM(payout_amount), 0) as total_payouts
       FROM investor_payouts 
       WHERE investor_id = ? AND status = 'completed'`,
      [userId]
    );

    const availableBalance = balanceResult.rows[0].total_payouts || 0;

    if (amount > availableBalance) {
      return res.status(400).json({ 
        error: `Insufficient balance. Available: ${availableBalance}` 
      });
    }

    // Convert to cents for Stripe
    const amountInCents = Math.round(amount * 100);

    // Process withdrawal
    const withdrawal = await stripePaymentService.processWithdrawal({
      amount: amountInCents,
      currency,
      userId,
      paymentMethodId,
      description: `Withdrawal for investor ${userId}`,
    });

    // Record withdrawal in database
    await pool.query(
      `INSERT INTO investor_payouts (investment_id, revenue_distribution_id, investor_id, 
       payout_amount, payout_date, payment_method, transaction_id, status) 
       VALUES (0, 0, ?, ?, CURRENT_TIMESTAMP, 'stripe_withdrawal', ?, 'pending')`,
      [userId, -amount, withdrawal.transferId] // Negative amount for withdrawal
    );

    res.json({
      withdrawal: {
        id: withdrawal.transferId,
        amount,
        currency,
        status: withdrawal.status,
      },
      remainingBalance: availableBalance - amount
    });

  } catch (error) {
    console.error('Withdrawal creation error:', error);
    res.status(500).json({ 
      error: 'Failed to create withdrawal',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Get user's payment methods
 */
router.get('/payment-methods', authenticateToken, async (req, res) => {
  try {
    const userId = (req as any).user.id;

    // Get user's Stripe customer ID
    const userResult = await pool.query(
      'SELECT stripe_customer_id FROM users WHERE id = ?',
      [userId]
    );

    if (userResult.rows.length === 0) {
      return res.status(404).json({ error: 'User not found' });
    }

    const stripeCustomerId = userResult.rows[0].stripe_customer_id;

    if (!stripeCustomerId) {
      return res.json({ paymentMethods: [] });
    }

    // Get payment methods from Stripe
    const paymentMethods = await stripePaymentService.getCustomerPaymentMethods(stripeCustomerId);

    res.json({ paymentMethods });

  } catch (error) {
    console.error('Get payment methods error:', error);
    res.status(500).json({ 
      error: 'Failed to retrieve payment methods',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Save new payment method
 */
router.post('/save-payment-method', authenticateToken, async (req, res) => {
  try {
    const { paymentMethodId } = req.body;
    const userId = (req as any).user.id;

    if (!paymentMethodId) {
      return res.status(400).json({ 
        error: 'Payment method ID is required' 
      });
    }

    // Get or create Stripe customer
    let stripeCustomerId;
    const userResult = await pool.query(
      'SELECT stripe_customer_id, email, first_name, last_name FROM users WHERE id = ?',
      [userId]
    );

    if (userResult.rows.length === 0) {
      return res.status(404).json({ error: 'User not found' });
    }

    const user = userResult.rows[0];
    stripeCustomerId = user.stripe_customer_id;

    if (!stripeCustomerId) {
      // Create Stripe customer
      const customerName = `${user.first_name || ''} ${user.last_name || ''}`.trim();
      stripeCustomerId = await stripePaymentService.createCustomer(
        userId, 
        user.email, 
        customerName || undefined
      );

      // Update user record with Stripe customer ID
      await pool.query(
        'UPDATE users SET stripe_customer_id = ? WHERE id = ?',
        [stripeCustomerId, userId]
      );
    }

    // Save payment method
    const savedPaymentMethod = await stripePaymentService.savePaymentMethod(
      stripeCustomerId, 
      paymentMethodId
    );

    res.json({ paymentMethod: savedPaymentMethod });

  } catch (error) {
    console.error('Save payment method error:', error);
    res.status(500).json({ 
      error: 'Failed to save payment method',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Get payment history
 */
router.get('/history', authenticateToken, async (req, res) => {
  try {
    const userId = (req as any).user.id;
    const { limit = 20, offset = 0 } = req.query;

    // Get investment payments
    const investmentResult = await pool.query(
      `SELECT i.id, i.amount, i.investment_date, i.status, i.transaction_id,
              d.title as dro_title, d.industry
       FROM investments i
       JOIN dros d ON i.dro_id = d.id
       WHERE i.investor_id = ?
       ORDER BY i.investment_date DESC
       LIMIT ? OFFSET ?`,
      [userId, limit, offset]
    );

    // Get payout history
    const payoutResult = await pool.query(
      `SELECT ip.id, ip.payout_amount, ip.payout_date, ip.status, ip.transaction_id,
              rd.distribution_date, d.title as dro_title
       FROM investor_payouts ip
       LEFT JOIN revenue_distributions rd ON ip.revenue_distribution_id = rd.id
       LEFT JOIN dros d ON rd.dro_id = d.id
       WHERE ip.investor_id = ?
       ORDER BY ip.payout_date DESC
       LIMIT ? OFFSET ?`,
      [userId, limit, offset]
    );

    res.json({
      investments: investmentResult.rows,
      payouts: payoutResult.rows,
      pagination: {
        limit: parseInt(limit as string),
        offset: parseInt(offset as string),
      }
    });

  } catch (error) {
    console.error('Payment history error:', error);
    res.status(500).json({ 
      error: 'Failed to retrieve payment history',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Webhook endpoint for Stripe events
 */
router.post('/webhook/stripe', rawBodyMiddleware, async (req, res) => {
  try {
    const signature = req.headers['stripe-signature'] as string;

    if (!signature) {
      return res.status(400).json({ error: 'Missing Stripe signature' });
    }

    // Handle webhook
    await stripePaymentService.handleWebhook(req.body.toString(), signature);

    res.json({ received: true });

  } catch (error) {
    console.error('Stripe webhook error:', error);
    res.status(400).json({ 
      error: 'Webhook processing failed',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Get payment intent details
 */
router.get('/payment-intent/:id', authenticateToken, async (req, res) => {
  try {
    const { id } = req.params;
    const userId = (req as any).user.id;

    // Verify the payment intent belongs to this user
    const investmentResult = await pool.query(
      'SELECT id, dro_id, amount FROM investments WHERE transaction_id = ? AND investor_id = ?',
      [id, userId]
    );

    if (investmentResult.rows.length === 0) {
      return res.status(404).json({ 
        error: 'Payment intent not found or unauthorized' 
      });
    }

    // Get payment intent from Stripe
    const paymentIntent = await stripePaymentService.getPaymentIntent(id);

    res.json({
      paymentIntent,
      investment: investmentResult.rows[0]
    });

  } catch (error) {
    console.error('Get payment intent error:', error);
    res.status(500).json({ 
      error: 'Failed to retrieve payment intent',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

export default router;

