// ============= PAYPAL ROUTES =============

/**
 * Create PayPal order for DRO investment
 */
router.post('/paypal/create-order', authenticateToken, async (req, res) => {
  try {
    const { droId, amount, currency = 'USD' } = req.body;
    const userId = (req as any).user.id;

    // Validate input
    if (!droId || !amount || amount <= 0) {
      return res.status(400).json({ 
        error: 'Valid DRO ID and amount are required' 
      });
    }

    // Verify DRO exists and is active
    const droResult = await pool.query(
      'SELECT id, title, minimum_investment, target_amount, raised_amount, status FROM dros WHERE id = ?',
      [droId]
    );

    if (droResult.rows.length === 0) {
      return res.status(404).json({ 
        error: 'DRO not found' 
      });
    }

    const dro = droResult.rows[0];

    if (dro.status !== 'active') {
      return res.status(400).json({ 
        error: 'DRO is not available for investment' 
      });
    }

    if (amount < dro.minimum_investment) {
      return res.status(400).json({ 
        error: `Minimum investment is ${dro.minimum_investment}` 
      });
    }

    // Create PayPal order
    const order = await paypalPaymentService.createOrder({
      amount,
      currency,
      userId,
      droId,
      description: `Investment in ${dro.title}`,
    });

    // Calculate fees
    const amountInCents = Math.round(amount * 100);
    const fees = paypalPaymentService.calculateFees(amountInCents, currency);

    // Create pending investment record
    await pool.query(
      `INSERT INTO investments (investor_id, dro_id, amount, ownership_percentage, 
       status, payment_method, transaction_id) 
       VALUES (?, ?, ?, ?, 'pending', 'paypal', ?)`,
      [
        userId, 
        droId, 
        amount,
        (amount / dro.target_amount) * 100, // Calculate ownership percentage
        order.id
      ]
    );

    res.json({
      order: {
        id: order.id,
        status: order.status,
        links: order.links,
      },
      fees: {
        platformFee: fees.platformFee / 100, // Convert back to dollars
        paypalFee: fees.paypalFee / 100,
        netAmount: fees.netAmount / 100,
      },
      dro: {
        id: dro.id,
        title: dro.title,
        minimumInvestment: dro.minimum_investment,
      }
    });

  } catch (error) {
    console.error('PayPal order creation error:', error);
    res.status(500).json({ 
      error: 'Failed to create PayPal order',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Capture PayPal order payment
 */
router.post('/paypal/capture-order', authenticateToken, async (req, res) => {
  try {
    const { orderId } = req.body;
    const userId = (req as any).user.id;

    if (!orderId) {
      return res.status(400).json({ 
        error: 'Order ID is required' 
      });
    }

    // Verify the order belongs to this user
    const investmentResult = await pool.query(
      'SELECT id, dro_id, amount FROM investments WHERE transaction_id = ? AND investor_id = ?',
      [orderId, userId]
    );

    if (investmentResult.rows.length === 0) {
      return res.status(404).json({ 
        error: 'Investment not found or unauthorized' 
      });
    }

    // Capture PayPal order
    const capturedOrder = await paypalPaymentService.captureOrder({
      orderId,
      userId,
    });

    // Update investment status
    await pool.query(
      'UPDATE investments SET status = ? WHERE transaction_id = ? AND investor_id = ?',
      ['completed', orderId, userId]
    );

    res.json({
      order: capturedOrder,
      investment: investmentResult.rows[0]
    });

  } catch (error) {
    console.error('PayPal capture error:', error);
    res.status(500).json({ 
      error: 'Failed to capture PayPal order',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Create PayPal payout for withdrawal
 */
router.post('/paypal/create-payout', authenticateToken, async (req, res) => {
  try {
    const { amount, currency = 'USD', recipientEmail } = req.body;
    const userId = (req as any).user.id;

    // Validate input
    if (!amount || amount <= 0 || !recipientEmail) {
      return res.status(400).json({ 
        error: 'Valid amount and recipient email are required' 
      });
    }

    // Check user's available balance
    const balanceResult = await pool.query(
      `SELECT COALESCE(SUM(payout_amount), 0) as total_payouts
       FROM investor_payouts 
       WHERE investor_id = ? AND status = 'completed'`,
      [userId]
    );

    const availableBalance = balanceResult.rows[0].total_payouts || 0;

    if (amount > availableBalance) {
      return res.status(400).json({ 
        error: `Insufficient balance. Available: ${availableBalance}` 
      });
    }

    // Process PayPal payout
    const payout = await paypalPaymentService.processPayout({
      amount,
      currency,
      userId,
      recipientEmail,
      description: `Withdrawal for investor ${userId}`,
    });

    // Record withdrawal in database
    await pool.query(
      `INSERT INTO investor_payouts (investment_id, revenue_distribution_id, investor_id, 
       payout_amount, payout_date, payment_method, transaction_id, status) 
       VALUES (0, 0, ?, ?, CURRENT_TIMESTAMP, 'paypal_payout', ?, 'pending')`,
      [userId, -amount, payout.payoutId] // Negative amount for withdrawal
    );

    res.json({
      payout: {
        id: payout.payoutId,
        amount,
        currency,
        status: payout.status,
      },
      remainingBalance: availableBalance - amount
    });

  } catch (error) {
    console.error('PayPal payout error:', error);
    res.status(500).json({ 
      error: 'Failed to create PayPal payout',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Get PayPal order details
 */
router.get('/paypal/order/:id', authenticateToken, async (req, res) => {
  try {
    const { id } = req.params;
    const userId = (req as any).user.id;

    // Verify the order belongs to this user
    const investmentResult = await pool.query(
      'SELECT id, dro_id, amount FROM investments WHERE transaction_id = ? AND investor_id = ?',
      [id, userId]
    );

    if (investmentResult.rows.length === 0) {
      return res.status(404).json({ 
        error: 'Order not found or unauthorized' 
      });
    }

    // Get order from PayPal
    const order = await paypalPaymentService.getOrder(id);

    res.json({
      order,
      investment: investmentResult.rows[0]
    });

  } catch (error) {
    console.error('Get PayPal order error:', error);
    res.status(500).json({ 
      error: 'Failed to retrieve PayPal order',
      message: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

/**
 * Webhook endpoint for PayPal events
 */
router.post('/webhook/paypal', express.json(), async (req, res) => {
  try {
    // Handle PayPal webhook
    await paypalPaymentService.handleWebhook(req.body, req.headers);

