   * Handle payment denied webhook
   */
  private async handlePaymentDenied(resource: any): Promise<void> {
    console.log('âŒ PayPal payment denied:', {
      id: resource.id,
      amount: resource.amount?.value,
      currency: resource.amount?.currency_code,
    });

    // TODO: Update investment record status to failed
    // TODO: Send failure notification email
    // TODO: Create audit log entry
  }

  /**
   * Handle payment created webhook
   */
  private async handlePaymentCreated(resource: any): Promise<void> {
    console.log('ğŸ’³ PayPal payment created:', {
      id: resource.id,
      state: resource.state,
    });

    // TODO: Log payment creation
  }

  /**
   * Calculate PayPal fees (typically 2.9% + fixed fee)
   */
  calculateFees(amount: number, currency: string): { paypalFee: number; platformFee: number; netAmount: number } {
    // PayPal fees vary by currency and region
    const fixedFee = currency === 'USD' ? 30 : (currency === 'EUR' ? 35 : 30); // cents
    const paypalFee = Math.round(amount * 0.029 + fixedFee); // PayPal's typical fee
    const platformFee = Math.round(amount * 0.025); // Our 2.5% platform fee
    const netAmount = amount - paypalFee - platformFee;

    return {
      paypalFee,
      platformFee,
      netAmount,
    };
  }
}

