  /**
   * Handle failed payment webhook
   */
  private async handlePaymentFailed(paymentIntent: Stripe.PaymentIntent): Promise<void> {
    const { userId, droId, type } = paymentIntent.metadata;

    console.log('âŒ Payment failed:', {
