  }

  /**
   * Calculate platform fee (typically 2.9% + 30Â¢ for Stripe)
   */
  calculateFees(amount: number): { platformFee: number; stripeFee: number; netAmount: number } {
    const stripeFee = Math.round(amount * 0.029 + 30); // Stripe's typical fee
    const platformFee = Math.round(amount * 0.025); // Our 2.5% platform fee
    const netAmount = amount - stripeFee - platformFee;

    return {
      platformFee,
      stripeFee,
      netAmount,
    };
  }

  /**
   * Get payment intent details
   */
  async getPaymentIntent(paymentIntentId: string): Promise<PaymentIntent> {
