// Payment service types and interfaces
export interface PaymentMethod {
  id: string;
  type: string;
  card?: {
    brand: string;
    last4: string;
    exp_month: number;
    exp_year: number;
  };
}

export interface PaymentIntent {
  id: string;
  client_secret: string;
  amount: number;
  currency: string;
}

export interface PaymentHistory {
  investments: Investment[];
  payouts: Payout[];
  pagination: {
    limit: number;
    offset: number;
  };
}

export interface Investment {
  id: number;
  amount: number;
  investment_date: string;
  status: string;
  transaction_id: string;
  dro_title: string;
  industry: string;
}

export interface Payout {
  id: number;
  payout_amount: number;
  payout_date: string;
  status: string;
  transaction_id: string;
  distribution_date?: string;
  dro_title?: string;
}

export interface CreatePaymentIntentRequest {
  droId: number;
  amount: number;
  currency?: string;
}

export interface CreatePaymentIntentResponse {
  paymentIntent: PaymentIntent;
  fees: {
    platformFee: number;
    stripeFee: number;
    netAmount: number;
  };
  dro: {
    id: number;
    title: string;
    minimumInvestment: number;
  };
}

export interface PayPalOrder {
  id: string;
  status: string;
  links: Array<{
    href: string;
    rel: string;
    method: string;
  }>;
}

export interface CreatePayPalOrderResponse {
  order: PayPalOrder;
  fees: {
    platformFee: number;
    paypalFee: number;
    netAmount: number;
  };
  dro: {
    id: number;
    title: string;
    minimumInvestment: number;
  };
}

class PaymentService {
  private baseUrl = '/api/payment';

  private async request<T>(endpoint: string, options: RequestInit = {}): Promise<T> {
    const token = localStorage.getItem('authToken');

    const response = await fetch(`${this.baseUrl}${endpoint}`, {
      ...options,
      headers: {
        'Content-Type': 'application/json',
        'Authorization': token ? `Bearer ${token}` : '',
        ...options.headers,
      },
    });

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ error: 'Request failed' }));
      throw new Error(errorData.error || `HTTP ${response.status}`);
    }

    return response.json();
  }

  // ============= STRIPE METHODS =============

  async createPaymentIntent(data: CreatePaymentIntentRequest): Promise<CreatePaymentIntentResponse> {
    return this.request('/create-payment-intent', {
      method: 'POST',
      body: JSON.stringify(data),
    });
  }

  async confirmPayment(paymentIntentId: string, paymentMethodId: string): Promise<any> {
    return this.request('/confirm-payment', {
      method: 'POST',
      body: JSON.stringify({ paymentIntentId, paymentMethodId }),
    });
  }

  async createWithdrawal(amount: number, currency: string, paymentMethodId: string): Promise<any> {
    return this.request('/create-withdrawal', {
      method: 'POST',
      body: JSON.stringify({ amount, currency, paymentMethodId }),
    });
  }

  async getPaymentMethods(): Promise<PaymentMethod[]> {
    const response = await this.request<{ paymentMethods: PaymentMethod[] }>('/payment-methods');
    return response.paymentMethods;
  }

  async savePaymentMethod(paymentMethodId: string): Promise<PaymentMethod> {
    const response = await this.request<{ paymentMethod: PaymentMethod }>('/save-payment-method', {
      method: 'POST',
      body: JSON.stringify({ paymentMethodId }),
    });
    return response.paymentMethod;
  }

  async getPaymentIntent(id: string): Promise<any> {
    return this.request(`/payment-intent/${id}`);
  }

  // ============= PAYPAL METHODS =============

  async createPayPalOrder(data: CreatePaymentIntentRequest): Promise<CreatePayPalOrderResponse> {
    return this.request('/paypal/create-order', {
      method: 'POST',
      body: JSON.stringify(data),
    });
  }

  async capturePayPalOrder(orderId: string): Promise<any> {
    return this.request('/paypal/capture-order', {
      method: 'POST',
      body: JSON.stringify({ orderId }),
    });
  }

  async createPayPalPayout(amount: number, currency: string, recipientEmail: string): Promise<any> {
    return this.request('/paypal/create-payout', {
      method: 'POST',
      body: JSON.stringify({ amount, currency, recipientEmail }),
    });
  }

  async getPayPalOrder(id: string): Promise<any> {
    return this.request(`/paypal/order/${id}`);
  }

  // ============= GENERAL METHODS =============

  async getPaymentHistory(limit = 20, offset = 0): Promise<PaymentHistory> {
    return this.request(`/history?limit=${limit}&offset=${offset}`);
  }
}

// Stripe-specific service for frontend integration
export class StripePaymentService extends PaymentService {
  static async getPaymentMethods(): Promise<PaymentMethod[]> {
    const service = new StripePaymentService();
    return service.getPaymentMethods();
  }

  static async createPaymentIntent(data: CreatePaymentIntentRequest): Promise<CreatePaymentIntentResponse> {
    const service = new StripePaymentService();
    return service.createPaymentIntent(data);
  }

  static async savePaymentMethod(paymentMethodId: string): Promise<PaymentMethod> {
    const service = new StripePaymentService();
    return service.savePaymentMethod(paymentMethodId);
  }
}

// PayPal-specific service for frontend integration
export class PayPalPaymentService extends PaymentService {
  static async createOrder(data: CreatePaymentIntentRequest): Promise<CreatePayPalOrderResponse> {
    const service = new PayPalPaymentService();
    return service.createPayPalOrder(data);
  }

  static async captureOrder(orderId: string): Promise<any> {
    const service = new PayPalPaymentService();
    return service.capturePayPalOrder(orderId);
  }

  static async createPayout(amount: number, currency: string, recipientEmail: string): Promise<any> {
    const service = new PayPalPaymentService();
    return service.createPayPalPayout(amount, currency, recipientEmail);
  }
}

export const paymentService = new PaymentService();

