import React, { useState, useEffect } from 'react';
import { paymentService, PaymentHistory, Investment, Payout } from '../services/paymentService';

const PaymentHistoryComponent: React.FC = () => {
  const [history, setHistory] = useState<PaymentHistory | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'investments' | 'payouts'>('investments');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  useEffect(() => {
    loadPaymentHistory();
  }, [currentPage]);

  const loadPaymentHistory = async () => {
    try {
      setLoading(true);
      const offset = (currentPage - 1) * itemsPerPage;
      const data = await paymentService.getPaymentHistory(itemsPerPage, offset);
      setHistory(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load payment history');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(Math.abs(amount));
  };

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      completed: { color: 'bg-green-100 text-green-800', icon: 'âœ“' },
      pending: { color: 'bg-yellow-100 text-yellow-800', icon: 'â³' },
      failed: { color: 'bg-red-100 text-red-800', icon: 'âœ—' },
      processing: { color: 'bg-blue-100 text-blue-800', icon: 'âš¡' },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.pending;

    return (
      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${config.color}`}>
        <span className="mr-1">{config.icon}</span>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </span>
    );
  };

  const InvestmentRow: React.FC<{ investment: Investment }> = ({ investment }) => (
    <tr className="hover:bg-gray-50">
      <td className="px-6 py-4 whitespace-nowrap">
        <div className="flex items-center">
          <div className="text-2xl mr-3">ğŸ’°</div>
          <div>
            <div className="text-sm font-medium text-gray-900">
              {investment.dro_title}
            </div>
            <div className="text-sm text-gray-500">
              {investment.industry}
            </div>
          </div>
        </div>
      </td>
      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
        {formatCurrency(investment.amount)}
      </td>
      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
        {formatDate(investment.investment_date)}
      </td>
      <td className="px-6 py-4 whitespace-nowrap">
        {getStatusBadge(investment.status)}
      </td>
      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
        <span className="font-mono text-xs bg-gray-100 px-2 py-1 rounded">
          {investment.transaction_id.substring(0, 12)}...
        </span>
      </td>
    </tr>
  );

  const PayoutRow: React.FC<{ payout: Payout }> = ({ payout }) => (
    <tr className="hover:bg-gray-50">
      <td className="px-6 py-4 whitespace-nowrap">
        <div className="flex items-center">
          <div className="text-2xl mr-3">
            {payout.payout_amount > 0 ? 'ğŸ“ˆ' : 'ğŸ’¸'}
          </div>
          <div>
            <div className="text-sm font-medium text-gray-900">
              {payout.payout_amount > 0 ? 'Revenue Distribution' : 'Withdrawal'}
            </div>
            <div className="text-sm text-gray-500">
              {payout.dro_title || 'N/A'}
            </div>
          </div>
        </div>
      </td>
      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
        <span className={payout.payout_amount > 0 ? 'text-green-600' : 'text-red-600'}>
          {payout.payout_amount > 0 ? '+' : ''}
          {formatCurrency(payout.payout_amount)}
        </span>
      </td>
      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
        {formatDate(payout.payout_date)}
      </td>
      <td className="px-6 py-4 whitespace-nowrap">
        {getStatusBadge(payout.status)}
      </td>
      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
        <span className="font-mono text-xs bg-gray-100 px-2 py-1 rounded">
          {payout.transaction_id.substring(0, 12)}...
        </span>
      </td>
    </tr>
  );

  if (loading) {
    return (
