export const productionConfig = {
  // Server configuration
  server: {
    port: parseInt(process.env.PORT || '3001'),
    host: '0.0.0.0', // Required for Render.com
    env: 'production'
  },

  // Database configuration
  database: {
    path: process.env.DATABASE_PATH || '/opt/render/project/data/sirex.duckdb',
    backupEnabled: process.env.BACKUP_ENABLED === 'true',
    backupPath: process.env.CLOUDFLARE_R2_BACKUP_PATH || '/backups/',
    retentionDays: parseInt(process.env.BACKUP_RETENTION_DAYS || '30')
  },

  // CORS configuration for frontend
  cors: {
    origin: [
      process.env.FRONTEND_URL || 'https://sirex-frontend.onrender.com',
      /\.onrender\.com$/, // Allow all Render.com subdomains during development
    ],
    credentials: true,
    optionsSuccessStatus: 200
  },

  // Security configuration
  security: {
    jwtSecret: process.env.JWT_SECRET,
    jwtRefreshSecret: process.env.JWT_REFRESH_SECRET,
    jwtExpiresIn: process.env.JWT_EXPIRES_IN || '24h',
    jwtRefreshExpiresIn: process.env.JWT_REFRESH_EXPIRES_IN || '7d',
    otpSecret: process.env.OTP_SECRET,
    otpExpiresIn: parseInt(process.env.OTP_EXPIRES_IN || '300')
  },

  // Payment configuration
  payments: {
    stripe: {
      secretKey: process.env.STRIPE_SECRET_KEY,
      publishableKey: process.env.STRIPE_PUBLISHABLE_KEY,
      webhookSecret: process.env.STRIPE_WEBHOOK_SECRET,
      enabled: !!(process.env.STRIPE_SECRET_KEY && process.env.STRIPE_PUBLISHABLE_KEY)
    },
    paypal: {
      clientId: process.env.PAYPAL_CLIENT_ID,
      clientSecret: process.env.PAYPAL_CLIENT_SECRET,
      mode: process.env.PAYPAL_MODE || 'production',
      enabled: !!(process.env.PAYPAL_CLIENT_ID && process.env.PAYPAL_CLIENT_SECRET)
    }
  },

  // R2 Backup configuration
  r2: {
    enabled: process.env.BACKUP_ENABLED === 'true',
    accessKeyId: process.env.CLOUDFLARE_R2_ACCESS_KEY_ID,
    secretAccessKey: process.env.CLOUDFLARE_R2_SECRET_ACCESS_KEY,
    endpoint: process.env.CLOUDFLARE_R2_ENDPOINT,
    bucketName: process.env.CLOUDFLARE_R2_BUCKET_NAME || 'sirex',
    region: process.env.CLOUDFLARE_R2_REGION || 'auto',
    backupPath: process.env.CLOUDFLARE_R2_BACKUP_PATH || '/backups/'
  },

  // Logging configuration
  logging: {
    level: 'info',
    format: 'json', // Better for production log aggregation
    timestamp: true,
    requestLogging: true
  },

  // Health check configuration
  healthCheck: {
    path: '/health',
    timeout: 30000,
    interval: 60000
  },

  // Rate limiting
  rateLimit: {
    windowMs: 15 * 60 * 1000, // 15 minutes
    max: 1000, // Limit each IP to 1000 requests per windowMs
    message: 'Too many requests from this IP, please try again later',
    standardHeaders: true,
    legacyHeaders: false
  }
};

// Validation function to ensure all required config is present
export function validateProductionConfig(): boolean {
  const required = [
    'JWT_SECRET',
    'JWT_REFRESH_SECRET',
    'OTP_SECRET'
  ];

  const missing = required.filter(key => !process.env[key]);

  if (missing.length > 0) {
    console.error('âŒ Missing required environment variables:', missing);
    return false;
  }

  // Validate payment configuration
  const stripeConfigured = productionConfig.payments.stripe.enabled;
  const paypalConfigured = productionConfig.payments.paypal.enabled;

  if (!stripeConfigured && !paypalConfigured) {
    console.warn('âš ï¸  No payment gateways configured - payments will not work');
  }

  // Validate R2 backup configuration
  if (productionConfig.r2.enabled) {
    const r2Required = [
      'CLOUDFLARE_R2_ACCESS_KEY_ID',
      'CLOUDFLARE_R2_SECRET_ACCESS_KEY',
      'CLOUDFLARE_R2_ENDPOINT',
      'CLOUDFLARE_R2_BUCKET_NAME'
    ];

    const r2Missing = r2Required.filter(key => !process.env[key]);

    if (r2Missing.length > 0) {
      console.warn('âš ï¸  R2 backup enabled but missing config:', r2Missing);
    }
  }

  return true;
}

export default productionConfig;

