                <button
                  onClick={() => {
                    onNavigate('register');
                    setIsMobileMenuOpen(false);
                  }}
                  className="flex items-center space-x-3 px-4 py-3 rounded-xl text-base font-medium w-full text-white bg-gradient-primary hover:shadow-crypto transition-all duration-200"
                >
                  <Target className="w-5 h-5" />
