import React from 'react';
import type { DRO } from '../services/api';
import { Building2, TrendingUp, Users, Calendar, DollarSign, Target, Zap, Star, Shield, Award, Clock, Percent } from 'lucide-react';

interface DROCardProps {
  dro: DRO;
  onInvest: (dro: DRO) => void;
  onViewDetails: (dro: DRO) => void;
}

const DROCard: React.FC<DROCardProps> = ({ dro, onInvest, onViewDetails }) => {
  const fundingPercentage = dro.target_amount > 0 ? 
    ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0;

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const getStatusBadgeClass = (status: string) => {
    switch (status) {
      case 'active': return 'badge-active';
      case 'pending': return 'badge-pending';
      case 'completed': return 'badge-completed';
      case 'cancelled': return 'bg-gradient-to-r from-red-500 to-red-600 text-white px-3 py-1 rounded-full text-xs font-semibold uppercase tracking-wide';
      default: return 'bg-gradient-to-r from-gray-500 to-gray-600 text-white px-3 py-1 rounded-full text-xs font-semibold uppercase tracking-wide';
    }
  };

  const getIndustryIcon = (industry: string) => {
    const industryLower = industry.toLowerCase();
    if (industryLower.includes('tech') || industryLower.includes('software')) {
      return <Zap className="w-6 h-6 text-primary-400" />;
    } else if (industryLower.includes('food') || industryLower.includes('restaurant')) {
      return <div className="w-6 h-6 text-accent-gold text-xl">ðŸ½ï¸</div>;
    } else if (industryLower.includes('retail') || industryLower.includes('shop')) {
      return <div className="w-6 h-6 text-secondary-400 text-xl">ðŸ›ï¸</div>;
    } else if (industryLower.includes('health') || industryLower.includes('medical')) {
      return <Shield className="w-6 h-6 text-accent-green" />;
    } else if (industryLower.includes('education')) {
      return <div className="w-6 h-6 text-primary-400 text-xl">ðŸ“š</div>;
    } else {
      return <Building2 className="w-6 h-6 text-dark-500" />;
    }
  };

  const isHighFunding = fundingPercentage >= 75;
  const isHighReturn = dro.revenue_percentage >= 8;
  const isLowMinInvestment = dro.minimum_investment <= 5000;
  const isPremium = isHighFunding || isHighReturn;

  return (
    <div className={`glass-card card-hover shimmer group relative overflow-hidden border-2 ${
      isPremium ? 'border-gradient-primary shadow-2xl' : 'border-white/10'
    } transition-all duration-500`}>
      {/* Premium Badge */}
      {isPremium && (
        <div className="absolute top-4 right-4 z-10">
          <div className="bg-gradient-to-r from-accent-gold to-yellow-400 text-dark-900 px-3 py-1 rounded-full text-xs font-bold flex items-center space-x-1 shadow-lg">
            <Award className="w-3 h-3" />
            <span>PREMIUM</span>
          </div>
        </div>
      )}

      {/* Hot Deal Badge */}
      {isHighFunding && (
        <div className="absolute top-4 left-4 z-10">
          <div className="bg-gradient-to-r from-red-500 to-orange-500 text-white px-3 py-1 rounded-full text-xs font-bold flex items-center space-x-1 shadow-lg animate-pulse">
            <span>ðŸ”¥</span>
            <span>HOT DEAL</span>
          </div>
        </div>
      )}

      {/* Glow effect overlay */}
      <div className="absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none">
        <div className={`absolute inset-0 blur-xl ${
          isPremium 
            ? 'bg-gradient-to-r from-accent-gold/20 via-primary-500/20 to-secondary-500/20' 
            : 'bg-gradient-to-r from-primary-500/20 via-secondary-500/20 to-primary-500/20'
        }`}></div>
      </div>

      {/* Header */}
      <div className="relative p-6">
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-center space-x-4">
            <div className="p-3 rounded-xl bg-gradient-to-br from-primary-500/20 to-secondary-500/20 backdrop-blur-sm border border-white/10">
              {getIndustryIcon(dro.industry)}
            </div>
            <div>
              <h3 className="text-xl font-bold text-white mb-1 group-hover:text-gradient-primary transition-all duration-300">
                {dro.title}
              </h3>
              <p className="text-sm text-slate-300 flex items-center">
                <Building2 className="w-4 h-4 mr-2 text-primary-400" />
                {dro.msme_name || 'Unknown Company'}
              </p>
            </div>
          </div>
          <div className="flex flex-col items-end space-y-2">
            <span className={getStatusBadgeClass(dro.status)}>
              {dro.status?.charAt(0).toUpperCase() + dro.status?.slice(1)}
            </span>
            {fundingPercentage >= 75 && (
              <div className="flex items-center space-x-1 text-accent-gold">
                <Star className="w-3 h-3 fill-current" />
                <span className="text-xs font-semibold">Hot</span>
              </div>
            )}
          </div>
        </div>

        {/* Key Highlights */}
        <div className="mb-6">
          <div className="flex flex-wrap gap-2 mb-4">
            {isHighReturn && (
              <span className="inline-flex items-center px-2 py-1 rounded-lg text-xs font-semibold bg-accent-green/20 text-accent-green border border-accent-green/30">
                <TrendingUp className="w-3 h-3 mr-1" />
                High Return
              </span>
            )}
            {isLowMinInvestment && (
              <span className="inline-flex items-center px-2 py-1 rounded-lg text-xs font-semibold bg-primary-500/20 text-primary-300 border border-primary-500/30">
                <DollarSign className="w-3 h-3 mr-1" />
                Low Entry
              </span>
            )}
            {isHighFunding && (
              <span className="inline-flex items-center px-2 py-1 rounded-lg text-xs font-semibold bg-accent-gold/20 text-accent-gold border border-accent-gold/30">
                <Clock className="w-3 h-3 mr-1" />
                Fast Fill
              </span>
            )}
          </div>

          {dro.description && (
            <p className="text-slate-300 text-sm leading-relaxed line-clamp-2">
              {dro.description}
            </p>
          )}
        </div>

        {/* Revenue Highlight */}
        <div className="mb-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <span className="px-3 py-1.5 rounded-full text-xs font-semibold bg-gradient-to-r from-primary-500/20 to-secondary-500/20 text-primary-300 border border-primary-500/30">
                {dro.industry}
              </span>
            </div>
            <div className={`flex items-center space-x-2 px-4 py-2 rounded-xl ${
              isHighReturn 
                ? 'bg-gradient-to-r from-accent-green/20 to-emerald-500/20 border border-accent-green/30' 
                : 'bg-gradient-to-r from-primary-500/10 to-secondary-500/10 border border-primary-500/20'
            }`}>
              <Percent className={`w-5 h-5 ${
                isHighReturn ? 'text-accent-green' : 'text-primary-400'
              }`} />
              <div className="text-center">
                <div className={`text-xl font-bold ${
                  isHighReturn ? 'text-accent-green' : 'text-white'
                }`}>
                  {dro.revenue_percentage}%
                </div>
                <div className="text-xs text-slate-400 font-medium">Monthly Return</div>
              </div>
            </div>
          </div>
        </div>

        {/* Funding Progress */}
        <div className="mb-6">
          <div className="flex items-center justify-between text-sm mb-3">
            <span className="text-slate-300 font-medium">Funding Progress</span>
            <span className="font-bold text-white text-lg">{fundingPercentage.toFixed(1)}%</span>
          </div>
          <div className="progress-bar mb-3">
            <div 
              className="progress-fill"
              style={{ width: `${Math.min(fundingPercentage, 100)}%` }}
            ></div>
          </div>
          <div className="flex items-center justify-between text-sm">
            <div className="text-slate-400">
              <span className="text-white font-semibold">{formatCurrency(dro.raised_amount || 0)}</span>
              <span className="ml-1">raised</span>
            </div>
            <div className="text-slate-400">
              <span className="text-white font-semibold">{formatCurrency(dro.target_amount)}</span>
              <span className="ml-1">target</span>
            </div>
          </div>
        </div>

        {/* Enhanced Key Metrics */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <div className="col-span-2 p-4 rounded-xl bg-gradient-to-br from-white/10 to-white/5 backdrop-blur-sm border border-white/20">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="p-2 rounded-lg bg-gradient-to-br from-accent-gold/20 to-accent-gold/30">
                  <DollarSign className="w-5 h-5 text-accent-gold" />
                </div>
                <div>
                  <div className="text-lg font-bold text-white">
                    {formatCurrency(dro.minimum_investment)}
                  </div>
                  <div className="text-xs text-slate-400 font-medium">Minimum Investment</div>
                </div>
              </div>
              {isLowMinInvestment && (
                <div className="text-xs text-accent-green font-semibold bg-accent-green/20 px-2 py-1 rounded-lg">
                  Accessible
                </div>
              )}
            </div>
          </div>

          <div className="text-center p-3 rounded-xl bg-gradient-to-br from-white/5 to-white/10 backdrop-blur-sm border border-white/10">
            <div className="flex items-center justify-center mb-2">
              <div className="p-2 rounded-lg bg-gradient-to-br from-primary-500/20 to-primary-500/30">
                <Users className="w-4 h-4 text-primary-400" />
              </div>
            </div>
            <div className="text-lg font-bold text-white mb-1">
              {dro.investor_count || 0}
            </div>
            <div className="text-xs text-slate-400 font-medium">Investors</div>
          </div>

          <div className="text-center p-3 rounded-xl bg-gradient-to-br from-white/5 to-white/10 backdrop-blur-sm border border-white/10">
            <div className="flex items-center justify-center mb-2">
              <div className="p-2 rounded-lg bg-gradient-to-br from-secondary-500/20 to-secondary-500/30">
                <Calendar className="w-4 h-4 text-secondary-400" />
              </div>
            </div>
            <div className="text-lg font-bold text-white mb-1">
              {dro.duration_months}m
            </div>
            <div className="text-xs text-slate-400 font-medium">Duration</div>
          </div>
        </div>
      </div>

      {/* Enhanced Actions */}
      <div className="relative px-6 py-4 border-t border-white/10 bg-gradient-to-r from-white/5 to-white/10 backdrop-blur-sm">
        {dro.status === 'active' ? (
          <div className="space-y-3">
            <button
              onClick={() => onInvest(dro)}
              className={`w-full py-3 px-4 rounded-xl font-semibold transition-all duration-300 flex items-center justify-center space-x-2 ${
                isPremium
                  ? 'bg-gradient-to-r from-accent-gold to-yellow-400 text-dark-900 hover:from-yellow-400 hover:to-accent-gold shadow-lg glow-gold'
                  : 'btn-crypto-primary'
              } focus-ring`}
            >
              <Target className="w-5 h-5" />
              <span className="text-lg font-bold">
                {isPremium ? 'ðŸš€ Invest Premium' : 'Invest Now'}
              </span>
            </button>

            <button
              onClick={() => onViewDetails(dro)}
              className="w-full btn-crypto-secondary flex items-center justify-center space-x-2 focus-ring"
            >
              <span>View Full Details</span>
            </button>
          </div>
        ) : (
          <div className="text-center">
            <div className="text-slate-400 text-sm mb-2">Investment {dro.status}</div>
            <button
              onClick={() => onViewDetails(dro)}
              className="w-full btn-crypto-secondary flex items-center justify-center space-x-2 focus-ring"
            >
              <span>View Details</span>
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default DROCard;

