import React, { useState } from 'react';
import { authAPI, setAuthTokens, setCurrentUser } from '../services/api';
import { Eye, EyeOff, Mail, Lock, User, Building2, AlertCircle, CheckCircle } from 'lucide-react';

interface RegisterFormProps {
  onSuccess: () => void;
  onSwitchToLogin: () => void;
}

const RegisterForm: React.FC<RegisterFormProps> = ({ onSuccess, onSwitchToLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: '',
    userType: 'investor' as 'investor' | 'msme',
    companyName: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [acceptedTerms, setAcceptedTerms] = useState(false);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (error) setError(null);
  };

  const validateForm = () => {
    if (!formData.email) {
      setError('Email is required');
      return false;
    }
    if (!formData.email.includes('@')) {
      setError('Please enter a valid email address');
      return false;
    }
    if (!formData.password) {
      setError('Password is required');
      return false;
    }
    if (formData.password.length < 8) {
      setError('Password must be at least 8 characters long');
      return false;
    }
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return false;
    }
    if (!formData.name) {
      setError('Full name is required');
      return false;
    }
    if (formData.userType === 'msme' && !formData.companyName) {
      setError('Company name is required for MSME accounts');
      return false;
    }
    if (!acceptedTerms) {
      setError('You must accept the Terms of Service and Privacy Policy');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    try {
      setLoading(true);
      setError(null);

      const registrationData = {
        email: formData.email,
        password: formData.password,
        name: formData.name,
        user_type: formData.userType,
        ...(formData.userType === 'msme' && { company_name: formData.companyName }),
      };

      const response = await authAPI.register(registrationData);

      setAuthTokens(response.tokens);
      setCurrentUser(response.user);

      onSuccess();

    } catch (err: any) {
      console.error('Registration error:', err);
      const errorMessage = err.response?.data?.error || 'Registration failed. Please try again.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const getPasswordStrength = (password: string) => {
    let strength = 0;
    if (password.length >= 8) strength++;
    if (/[a-z]/.test(password)) strength++;
    if (/[A-Z]/.test(password)) strength++;
    if (/[0-9]/.test(password)) strength++;
    if (/[^A-Za-z0-9]/.test(password)) strength++;
    return strength;
  };

  const passwordStrength = getPasswordStrength(formData.password);
  const strengthColors = ['bg-red-500', 'bg-red-400', 'bg-yellow-500', 'bg-yellow-400', 'bg-green-500'];
  const strengthLabels = ['Very Weak', 'Weak', 'Fair', 'Good', 'Strong'];

  return (
    <div className="min-h-screen bg-gradient-dark flex flex-col justify-center py-12 sm:px-6 lg:px-8 relative overflow-hidden">
      {/* Background effects */}
      <div className="absolute inset-0 bg-gradient-mesh opacity-10"></div>
      <div className="absolute top-1/3 left-1/3 w-80 h-80 bg-secondary-500/20 rounded-full blur-3xl animate-float"></div>
      <div className="absolute bottom-1/3 right-1/3 w-72 h-72 bg-primary-500/20 rounded-full blur-3xl animate-float" style={{animationDelay: '3s'}}></div>
      <div className="sm:mx-auto sm:w-full sm:max-w-md relative z-10">
        <div className="text-center">
          <h1 className="text-4xl font-bold text-gradient-primary mb-2">SIREX</h1>
          <p className="text-sm text-slate-300 mt-1">Sand in River EX</p>
        </div>
        <h2 className="mt-8 text-center text-3xl font-extrabold text-white">
          Create your account
        </h2>
        <p className="mt-2 text-center text-sm text-slate-300">
          Already have an account?{' '}
          <button
            onClick={onSwitchToLogin}
            className="font-medium text-primary-400 hover:text-primary-300 transition-colors duration-200"
          >
            Sign in
          </button>
        </p>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md relative z-10">
        <div className="glass-card py-8 px-6 shadow-crypto-lg sm:px-10">
          <form className="space-y-6" onSubmit={handleSubmit}>
            {/* Account Type */}
            <div>
              <label className="block text-sm font-medium text-slate-200 mb-2">
                Account Type
              </label>
              <div className="grid grid-cols-2 gap-3">
                <button
                  type="button"
                  onClick={() => setFormData(prev => ({ ...prev, userType: 'investor' }))}
                  className={`p-4 glass border rounded-xl text-center transition-all duration-200 backdrop-blur-sm ${
                    formData.userType === 'investor'
                      ? 'border-primary-500 bg-primary-500/20 text-primary-300 shadow-crypto'
                      : 'border-white/20 text-slate-300 hover:border-white/40 hover:text-white hover:bg-white/10'
                  }`}
                  disabled={loading}
                >
                  <User className="w-5 h-5 mx-auto mb-2" />
                  <div className="text-sm font-medium">Investor</div>
                </button>
                <button
                  type="button"
                  onClick={() => setFormData(prev => ({ ...prev, userType: 'msme' }))}
                  className={`p-4 glass border rounded-xl text-center transition-all duration-200 backdrop-blur-sm ${
                    formData.userType === 'msme'
                      ? 'border-primary-500 bg-primary-500/20 text-primary-300 shadow-crypto'
                      : 'border-white/20 text-slate-300 hover:border-white/40 hover:text-white hover:bg-white/10'
                  }`}
                  disabled={loading}
                >
                  <Building2 className="w-5 h-5 mx-auto mb-2" />
                  <div className="text-sm font-medium">MSME</div>
                </button>
              </div>
            </div>

            {/* Full Name */}
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-slate-200 mb-2">
                Full Name
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <User className="h-5 w-5 text-slate-400" />
                </div>
                <input
                  id="name"
                  name="name"
                  type="text"
                  autoComplete="name"
                  required
                  value={formData.name}
                  onChange={handleInputChange}
                  className="appearance-none block w-full pl-10 pr-3 py-3 glass border border-white/20 rounded-xl placeholder-slate-400 text-slate-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm transition-all duration-200"
                  placeholder="Enter your full name"
                  disabled={loading}
                />
              </div>
            </div>

            {/* Company Name (for MSME) */}
            {formData.userType === 'msme' && (
              <div>
                <label htmlFor="companyName" className="block text-sm font-medium text-slate-200 mb-2">
                  Company Name
                </label>
                <div className="mt-1 relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <Building2 className="h-5 w-5 text-slate-400" />
                  </div>
                  <input
                    id="companyName"
                    name="companyName"
                    type="text"
                    autoComplete="organization"
                    required
                    value={formData.companyName}
                    onChange={handleInputChange}
                    className="appearance-none block w-full pl-10 pr-3 py-3 glass border border-white/20 rounded-xl placeholder-slate-400 text-slate-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm transition-all duration-200"
                    placeholder="Enter your company name"
                    disabled={loading}
                  />
                </div>
              </div>
            )}

            {/* Email */}
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-slate-200 mb-2">
                Email address
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Mail className="h-5 w-5 text-slate-400" />
                </div>
                <input
                  id="email"
                  name="email"
                  type="email"
                  autoComplete="email"
                  required
                  value={formData.email}
                  onChange={handleInputChange}
                  className="appearance-none block w-full pl-10 pr-3 py-3 glass border border-white/20 rounded-xl placeholder-slate-400 text-slate-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm transition-all duration-200"
                  placeholder="Enter your email"
                  disabled={loading}
                />
              </div>
            </div>

            {/* Password */}
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-slate-200 mb-2">
                Password
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Lock className="h-5 w-5 text-slate-400" />
                </div>
                <input
                  id="password"
                  name="password"
                  type={showPassword ? 'text' : 'password'}
                  autoComplete="new-password"
                  required
                  value={formData.password}
                  onChange={handleInputChange}
                  className="appearance-none block w-full pl-10 pr-10 py-3 glass border border-white/20 rounded-xl placeholder-slate-400 text-slate-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm transition-all duration-200"
                  placeholder="Create a password"
                  disabled={loading}
                />
                <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="text-slate-400 hover:text-slate-200 transition-colors duration-200"
                    disabled={loading}
                  >
                    {showPassword ? (
                      <EyeOff className="h-5 w-5" />
                    ) : (
                      <Eye className="h-5 w-5" />
                    )}
                  </button>
                </div>
              </div>

              {/* Password Strength */}
              {formData.password && (
                <div className="mt-2">
                  <div className="flex items-center space-x-2">
                    {[...Array(5)].map((_, i) => (
                      <div
                        key={i}
                        className={`h-1 flex-1 rounded ${
                          i < passwordStrength ? strengthColors[passwordStrength - 1] : 'bg-white/20'
                        }`}
                      />
                    ))}
                  </div>
                  <div className="text-xs text-slate-400 mt-1">
                    Password strength: {strengthLabels[passwordStrength - 1] || 'Too weak'}
                  </div>
                </div>
              )}
            </div>

            {/* Confirm Password */}
            <div>
              <label htmlFor="confirmPassword" className="block text-sm font-medium text-slate-200 mb-2">
                Confirm Password
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Lock className="h-5 w-5 text-slate-400" />
                </div>
                <input
                  id="confirmPassword"
                  name="confirmPassword"
                  type={showConfirmPassword ? 'text' : 'password'}
                  autoComplete="new-password"
                  required
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  className="appearance-none block w-full pl-10 pr-10 py-3 glass border border-white/20 rounded-xl placeholder-slate-400 text-slate-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm transition-all duration-200"
                  placeholder="Confirm your password"
                  disabled={loading}
                />
                <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
                  <button
                    type="button"
                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    className="text-slate-400 hover:text-slate-200 transition-colors duration-200"
                    disabled={loading}
                  >
                    {showConfirmPassword ? (
                      <EyeOff className="h-5 w-5" />
                    ) : (
                      <Eye className="h-5 w-5" />
                    )}
                  </button>
                </div>
              </div>
            </div>

            {/* Terms and Conditions */}
            <div className="flex items-start">
              <input
                id="acceptedTerms"
                name="acceptedTerms"
                type="checkbox"
                checked={acceptedTerms}
                onChange={(e) => setAcceptedTerms(e.target.checked)}
                className="h-4 w-4 text-primary-500 focus:ring-primary-500 border-white/30 rounded bg-white/10 mt-1"
                disabled={loading}
              />
              <label htmlFor="acceptedTerms" className="ml-2 block text-sm text-slate-200">
                I agree to the{' '}
                <button type="button" className="font-medium text-primary-400 hover:text-primary-300 transition-colors duration-200">
                  Terms of Service
                </button>{' '}
                and{' '}
                <button type="button" className="font-medium text-primary-400 hover:text-primary-300 transition-colors duration-200">
                  Privacy Policy
                </button>
              </label>
            </div>

            {/* Error Message */}
            {error && (
              <div className="glass bg-red-500/10 border border-red-400/30 rounded-xl p-4 backdrop-blur-sm">
                <div className="flex">
                  <AlertCircle className="h-5 w-5 text-red-300" />
                  <div className="ml-3">
                    <h3 className="text-sm font-medium text-red-200">Registration Failed</h3>
                    <div className="mt-2 text-sm text-red-300">{error}</div>
                  </div>
                </div>
              </div>
            )}

            {/* Submit Button */}
            <div>
              <button
                type="submit"
                disabled={loading}
                className="w-full flex justify-center items-center py-3 px-4 border border-transparent rounded-xl shadow-crypto text-sm font-medium text-white btn-crypto-primary focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200"
              >
                {loading ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Creating Account...
                  </>
                ) : (
                  'Create Account'
                )}
              </button>
            </div>
          </form>

          {/* Security Notice */}
          <div className="mt-6">
            <div className="glass bg-primary-500/10 border border-primary-400/30 rounded-xl p-4 backdrop-blur-sm">
              <div className="flex">
                <CheckCircle className="h-5 w-5 text-primary-300" />
                <div className="ml-3">
                  <h3 className="text-sm font-medium text-primary-200">Secure Registration</h3>
                  <div className="mt-1 text-sm text-primary-300">
                    Your information is encrypted and protected. MSME accounts will require KYC verification before creating DROs.
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RegisterForm;

import React, { useState, useEffect } from 'react';
import type { DRO, Investment } from '../services/api';
import { droAPI } from '../services/api';
import { 
  X, 
  Building2, 
  TrendingUp, 
  Users, 
  Calendar, 
  DollarSign, 
  Target,
  BarChart3,
  Info
} from 'lucide-react';

interface DRODetailModalProps {
  dro: DRO;
  onClose: () => void;
  onInvest: (dro: DRO) => void;
}

const DRODetailModal: React.FC<DRODetailModalProps> = ({ dro, onClose, onInvest }) => {
  const [detailedDRO, setDetailedDRO] = useState<DRO | null>(null);
  const [investments, setInvestments] = useState<Investment[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDRODetails = async () => {
      try {
        const response = await droAPI.getDRO(dro.id);
        setDetailedDRO(response.dro);
        setInvestments(response.investments);
      } catch (error) {
        console.error('Error fetching DRO details:', error);
        // Fallback to basic DRO data
        setDetailedDRO(dro);
        setInvestments([]);
      } finally {
        setLoading(false);
      }
    };

    fetchDRODetails();
  }, [dro.id]);

  const displayDRO = detailedDRO || dro;
  const fundingPercentage = displayDRO.target_amount > 0 ? 
    ((displayDRO.raised_amount || 0) / displayDRO.target_amount) * 100 : 0;

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'completed': return 'bg-blue-100 text-blue-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getIndustryIcon = (industry: string) => {
    const industryLower = industry.toLowerCase();
    if (industryLower.includes('tech') || industryLower.includes('software')) {
      return 'ðŸ’»';
    } else if (industryLower.includes('food') || industryLower.includes('restaurant')) {
      return 'ðŸ½ï¸';
    } else if (industryLower.includes('retail') || industryLower.includes('shop')) {
      return 'ðŸ›ï¸';
    } else if (industryLower.includes('health') || industryLower.includes('medical')) {
      return 'ðŸ¥';
    } else if (industryLower.includes('education')) {
      return 'ðŸ“š';
    } else {
      return 'ðŸ¢';
    }
  };

  return (
    <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-50">
      <div className="glass-card max-w-4xl w-full max-h-[90vh] overflow-y-auto shadow-crypto-lg border border-white/10">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-white/10">
          <div className="flex items-center space-x-3">
            <div className="text-3xl">{getIndustryIcon(displayDRO.industry)}</div>
            <div>
              <h2 className="text-2xl font-semibold text-white">{displayDRO.title}</h2>
              <div className="flex items-center space-x-4 mt-1">
                <p className="text-slate-300 flex items-center">
                  <Building2 className="w-4 h-4 mr-1" />
                  {displayDRO.msme_name || 'Unknown Company'}
                </p>
                <span className={`px-3 py-1 rounded-full text-xs font-medium ${getStatusColor(displayDRO.status)}`}>
                  {displayDRO.status?.charAt(0).toUpperCase() + displayDRO.status?.slice(1)}
                </span>
              </div>
            </div>
          </div>
          <button
            onClick={onClose}
            className="text-slate-400 hover:text-white transition-colors duration-200"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="p-6">
          {/* Description */}
          {displayDRO.description && (
            <div className="mb-8">
              <h3 className="text-lg font-semibold text-white mb-3">About This Opportunity</h3>
              <p className="text-slate-300 leading-relaxed">{displayDRO.description}</p>
            </div>
          )}

          {/* Key Metrics Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="glass bg-primary-500/10 border border-primary-500/30 rounded-xl p-4 backdrop-blur-sm">
              <div className="flex items-center justify-between mb-2">
                <TrendingUp className="w-6 h-6 text-primary-400" />
                <span className="text-2xl font-bold text-primary-400">
                  {displayDRO.revenue_percentage}%
                </span>
              </div>
              <div className="text-sm text-slate-400">Revenue Share</div>
            </div>

            <div className="glass bg-accent-green/10 border border-accent-green/30 rounded-xl p-4 backdrop-blur-sm">
              <div className="flex items-center justify-between mb-2">
                <DollarSign className="w-6 h-6 text-accent-green" />
                <span className="text-lg font-bold text-accent-green">
                  {formatCurrency(displayDRO.minimum_investment)}
                </span>
              </div>
              <div className="text-sm text-slate-400">Min Investment</div>
            </div>

            <div className="glass bg-secondary-500/10 border border-secondary-500/30 rounded-xl p-4 backdrop-blur-sm">
              <div className="flex items-center justify-between mb-2">
                <Users className="w-6 h-6 text-secondary-400" />
                <span className="text-2xl font-bold text-secondary-400">
                  {displayDRO.investor_count || 0}
                </span>
              </div>
              <div className="text-sm text-slate-400">Investors</div>
            </div>

            <div className="glass bg-accent-gold/10 border border-accent-gold/30 rounded-xl p-4 backdrop-blur-sm">
              <div className="flex items-center justify-between mb-2">
                <Calendar className="w-6 h-6 text-accent-gold" />
                <span className="text-2xl font-bold text-accent-gold">
                  {displayDRO.duration_months}
                </span>
              </div>
              <div className="text-sm text-slate-400">Months Duration</div>
            </div>
          </div>

          {/* Funding Progress */}
          <div className="mb-8">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-white flex items-center">
                <Target className="w-5 h-5 mr-2" />
                Funding Progress
              </h3>
              <span className="text-lg font-bold text-primary-400">
                {fundingPercentage.toFixed(1)}%
              </span>
            </div>

            <div className="progress-bar mb-4">
              <div 
                className="progress-fill h-4 rounded-full transition-all duration-500"
                style={{ width: `${Math.min(fundingPercentage, 100)}%` }}
              ></div>
            </div>

            <div className="grid grid-cols-3 gap-4 text-center">
              <div>
                <div className="text-2xl font-bold text-white">
                  {formatCurrency(displayDRO.raised_amount || 0)}
                </div>
                <div className="text-sm text-slate-400">Raised</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-primary-400">
                  {formatCurrency(displayDRO.target_amount)}
                </div>
                <div className="text-sm text-slate-400">Target</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-accent-green">
                  {formatCurrency(displayDRO.target_amount - (displayDRO.raised_amount || 0))}
                </div>
                <div className="text-sm text-slate-400">Remaining</div>
              </div>
            </div>
          </div>

          {/* Investment Details */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
            {/* Investment Terms */}
            <div>
              <h3 className="text-lg font-semibold text-white mb-4 flex items-center">
                <Info className="w-5 h-5 mr-2" />
                Investment Terms
              </h3>
              <div className="space-y-3">
                <div className="flex justify-between py-2 border-b border-white/10">
                  <span className="text-slate-400">Industry</span>
                  <span className="font-medium text-white">{displayDRO.industry}</span>
                </div>
                <div className="flex justify-between py-2 border-b border-white/10">
                  <span className="text-slate-400">Revenue Share</span>
                  <span className="font-medium text-primary-400">{displayDRO.revenue_percentage}%</span>
                </div>
                <div className="flex justify-between py-2 border-b border-white/10">
                  <span className="text-slate-400">Minimum Investment</span>
                  <span className="font-medium text-white">{formatCurrency(displayDRO.minimum_investment)}</span>
                </div>
                <div className="flex justify-between py-2 border-b border-white/10">
                  <span className="text-slate-400">Duration</span>
                  <span className="font-medium text-white">{displayDRO.duration_months} months</span>
                </div>
                <div className="flex justify-between py-2 border-b border-white/10">
                  <span className="text-slate-400">Created</span>
                  <span className="font-medium text-white">{formatDate(displayDRO.created_at)}</span>
                </div>
              </div>
            </div>

            {/* Recent Investors */}
            <div>
              <h3 className="text-lg font-semibold text-white mb-4 flex items-center">
                <BarChart3 className="w-5 h-5 mr-2" />
                Recent Investors
              </h3>
              {loading ? (
                <div className="space-y-3">
                  {[1, 2, 3].map((i) => (
                    <div key={i} className="animate-pulse">
                      <div className="h-4 bg-white/20 rounded mb-2"></div>
                      <div className="h-3 bg-white/20 rounded w-3/4"></div>
                    </div>
                  ))}
                </div>
              ) : investments.length > 0 ? (
                <div className="space-y-3">
                  {investments.slice(0, 5).map((investment, index) => (
                    <div key={index} className="flex justify-between items-center py-2 border-b border-white/10">
                      <div>
                        <div className="font-medium text-white">
                          {investment.investor_name || 'Anonymous Investor'}
                        </div>
                        <div className="text-sm text-slate-400">
                          {formatDate(investment.investment_date)}
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="font-medium text-primary-400">
                          {formatCurrency(investment.amount)}
                        </div>
                        <div className="text-xs text-slate-400 capitalize">
                          {investment.status}
                        </div>
                      </div>
                    </div>
                  ))}
                  {investments.length > 5 && (
                    <div className="text-center text-sm text-slate-400 pt-2">
                      +{investments.length - 5} more investors
                    </div>
                  )}
                </div>
              ) : (
                <div className="text-center py-8 text-slate-400">
                  <Users className="w-12 h-12 mx-auto mb-2 text-slate-500" />
                  <p>No investors yet</p>
                  <p className="text-sm">Be the first to invest!</p>
                </div>
              )}
            </div>
          </div>

          {/* Risk Notice */}
          <div className="glass bg-accent-gold/10 border border-accent-gold/30 rounded-xl p-4 mb-6 backdrop-blur-sm">
            <div className="flex items-start">
              <div className="text-accent-gold mr-3 mt-1">âš ï¸</div>
              <div>
                <h4 className="font-medium text-accent-gold mb-1">Investment Risk Notice</h4>
                <p className="text-sm text-slate-300">
                  Revenue-based investments carry inherent risks. Returns are not guaranteed and depend on the business's 
                  actual revenue performance. You should carefully consider your risk tolerance and investment objectives 
                  before investing.
                </p>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex space-x-4">
            <button
              onClick={onClose}
              className="flex-1 btn-crypto-secondary"
            >
              Close
            </button>

            {displayDRO.status === 'active' && (
              <button
                onClick={() => onInvest(displayDRO)}
                className="flex-1 btn-crypto-primary flex items-center justify-center"
              >
                <Target className="w-5 h-5 mr-2" />
                Invest Now
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DRODetailModal;

