            <div className="glass bg-primary-500/10 border border-primary-400/30 rounded-xl p-4 backdrop-blur-sm">
              <div className="flex">
                <CheckCircle className="h-5 w-5 text-primary-300" />
                <div className="ml-3">
                  <h3 className="text-sm font-medium text-primary-200">Secure Login</h3>
                  <div className="mt-1 text-sm text-primary-300">
                    Your login is protected with industry-standard security measures including encryption and secure token management.
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;

import React, { useState } from 'react';
import { authAPI, setAuthTokens, setCurrentUser } from '../services/api';
import { Eye, EyeOff, Mail, Lock, User, Building2, AlertCircle, CheckCircle } from 'lucide-react';

interface RegisterFormProps {
  onSuccess: () => void;
  onSwitchToLogin: () => void;
}

const RegisterForm: React.FC<RegisterFormProps> = ({ onSuccess, onSwitchToLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    name: '',
    userType: 'investor' as 'investor' | 'msme',
    companyName: '',
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [acceptedTerms, setAcceptedTerms] = useState(false);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (error) setError(null);
  };

  const validateForm = () => {
    if (!formData.email) {
      setError('Email is required');
      return false;
    }
    if (!formData.email.includes('@')) {
      setError('Please enter a valid email address');
      return false;
    }
    if (!formData.password) {
      setError('Password is required');
      return false;
    }
    if (formData.password.length < 8) {
      setError('Password must be at least 8 characters long');
      return false;
    }
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return false;
    }
    if (!formData.name) {
      setError('Full name is required');
      return false;
    }
    if (formData.userType === 'msme' && !formData.companyName) {
      setError('Company name is required for MSME accounts');
      return false;
    }
    if (!acceptedTerms) {
      setError('You must accept the Terms of Service and Privacy Policy');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    try {
      setLoading(true);
      setError(null);

      const registrationData = {
        email: formData.email,
        password: formData.password,
        name: formData.name,
        user_type: formData.userType,
        ...(formData.userType === 'msme' && { company_name: formData.companyName }),
      };

      const response = await authAPI.register(registrationData);

      setAuthTokens(response.tokens);
      setCurrentUser(response.user);

      onSuccess();

    } catch (err: any) {
      console.error('Registration error:', err);
      const errorMessage = err.response?.data?.error || 'Registration failed. Please try again.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const getPasswordStrength = (password: string) => {
    let strength = 0;
    if (password.length >= 8) strength++;
    if (/[a-z]/.test(password)) strength++;
    if (/[A-Z]/.test(password)) strength++;
    if (/[0-9]/.test(password)) strength++;
    if (/[^A-Za-z0-9]/.test(password)) strength++;
    return strength;
  };

  const passwordStrength = getPasswordStrength(formData.password);
  const strengthColors = ['bg-red-500', 'bg-red-400', 'bg-yellow-500', 'bg-yellow-400', 'bg-green-500'];
  const strengthLabels = ['Very Weak', 'Weak', 'Fair', 'Good', 'Strong'];

  return (
    <div className="min-h-screen bg-gradient-dark flex flex-col justify-center py-12 sm:px-6 lg:px-8 relative overflow-hidden">
      {/* Background effects */}
      <div className="absolute inset-0 bg-gradient-mesh opacity-10"></div>
      <div className="absolute top-1/3 left-1/3 w-80 h-80 bg-secondary-500/20 rounded-full blur-3xl animate-float"></div>
      <div className="absolute bottom-1/3 right-1/3 w-72 h-72 bg-primary-500/20 rounded-full blur-3xl animate-float" style={{animationDelay: '3s'}}></div>
      <div className="sm:mx-auto sm:w-full sm:max-w-md relative z-10">
        <div className="text-center">
          <h1 className="text-4xl font-bold text-gradient-primary mb-2">SIREX</h1>
          <p className="text-sm text-slate-300 mt-1">Sand in River EX</p>
        </div>
        <h2 className="mt-8 text-center text-3xl font-extrabold text-white">
          Create your account
        </h2>
        <p className="mt-2 text-center text-sm text-slate-300">
          Already have an account?{' '}
          <button
            onClick={onSwitchToLogin}
            className="font-medium text-primary-400 hover:text-primary-300 transition-colors duration-200"
          >
            Sign in
          </button>
        </p>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md relative z-10">
        <div className="glass-card py-8 px-6 shadow-crypto-lg sm:px-10">
          <form className="space-y-6" onSubmit={handleSubmit}>
            {/* Account Type */}
            <div>
              <label className="block text-sm font-medium text-slate-200 mb-2">
                Account Type
              </label>
              <div className="grid grid-cols-2 gap-3">
                <button
                  type="button"
                  onClick={() => setFormData(prev => ({ ...prev, userType: 'investor' }))}
                  className={`p-4 glass border rounded-xl text-center transition-all duration-200 backdrop-blur-sm ${
                    formData.userType === 'investor'
                      ? 'border-primary-500 bg-primary-500/20 text-primary-300 shadow-crypto'
                      : 'border-white/20 text-slate-300 hover:border-white/40 hover:text-white hover:bg-white/10'
                  }`}
                  disabled={loading}
                >
                  <User className="w-5 h-5 mx-auto mb-2" />
                  <div className="text-sm font-medium">Investor</div>
                </button>
                <button
                  type="button"
                  onClick={() => setFormData(prev => ({ ...prev, userType: 'msme' }))}
                  className={`p-4 glass border rounded-xl text-center transition-all duration-200 backdrop-blur-sm ${
                    formData.userType === 'msme'
                      ? 'border-primary-500 bg-primary-500/20 text-primary-300 shadow-crypto'
                      : 'border-white/20 text-slate-300 hover:border-white/40 hover:text-white hover:bg-white/10'
                  }`}
                  disabled={loading}
                >
                  <Building2 className="w-5 h-5 mx-auto mb-2" />
                  <div className="text-sm font-medium">MSME</div>
                </button>
              </div>
            </div>

            {/* Full Name */}
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-slate-200 mb-2">
                Full Name
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <User className="h-5 w-5 text-slate-400" />
                </div>
                <input
                  id="name"
                  name="name"
                  type="text"
                  autoComplete="name"
                  required
                  value={formData.name}
                  onChange={handleInputChange}
                  className="appearance-none block w-full pl-10 pr-3 py-3 glass border border-white/20 rounded-xl placeholder-slate-400 text-slate-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm transition-all duration-200"
                  placeholder="Enter your full name"
                  disabled={loading}
                />
              </div>
            </div>

            {/* Company Name (for MSME) */}
            {formData.userType === 'msme' && (
              <div>
                <label htmlFor="companyName" className="block text-sm font-medium text-slate-200 mb-2">
                  Company Name
                </label>
                <div className="mt-1 relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <Building2 className="h-5 w-5 text-slate-400" />
                  </div>
                  <input
                    id="companyName"
                    name="companyName"
                    type="text"
                    autoComplete="organization"
                    required
                    value={formData.companyName}
                    onChange={handleInputChange}
                    className="appearance-none block w-full pl-10 pr-3 py-3 glass border border-white/20 rounded-xl placeholder-slate-400 text-slate-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm transition-all duration-200"
                    placeholder="Enter your company name"
                    disabled={loading}
                  />
                </div>
              </div>
            )}

            {/* Email */}
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-slate-200 mb-2">
                Email address
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Mail className="h-5 w-5 text-slate-400" />
                </div>
                <input
                  id="email"
                  name="email"
                  type="email"
                  autoComplete="email"
                  required
                  value={formData.email}
                  onChange={handleInputChange}
                  className="appearance-none block w-full pl-10 pr-3 py-3 glass border border-white/20 rounded-xl placeholder-slate-400 text-slate-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm transition-all duration-200"
                  placeholder="Enter your email"
                  disabled={loading}
                />
              </div>
            </div>

            {/* Password */}
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-slate-200 mb-2">
                Password
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Lock className="h-5 w-5 text-slate-400" />
                </div>
                <input
                  id="password"
                  name="password"
                  type={showPassword ? 'text' : 'password'}
                  autoComplete="new-password"
                  required
                  value={formData.password}
                  onChange={handleInputChange}
                  className="appearance-none block w-full pl-10 pr-10 py-3 glass border border-white/20 rounded-xl placeholder-slate-400 text-slate-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm transition-all duration-200"
                  placeholder="Create a password"
                  disabled={loading}
                />
                <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="text-slate-400 hover:text-slate-200 transition-colors duration-200"
                    disabled={loading}
                  >
                    {showPassword ? (
                      <EyeOff className="h-5 w-5" />
                    ) : (
                      <Eye className="h-5 w-5" />
                    )}
                  </button>
                </div>
              </div>

              {/* Password Strength */}
              {formData.password && (
                <div className="mt-2">
                  <div className="flex items-center space-x-2">
                    {[...Array(5)].map((_, i) => (
                      <div
                        key={i}
                        className={`h-1 flex-1 rounded ${
                          i < passwordStrength ? strengthColors[passwordStrength - 1] : 'bg-white/20'
                        }`}
                      />
                    ))}
                  </div>
                  <div className="text-xs text-slate-400 mt-1">
                    Password strength: {strengthLabels[passwordStrength - 1] || 'Too weak'}
                  </div>
                </div>
              )}
            </div>

            {/* Confirm Password */}
            <div>
              <label htmlFor="confirmPassword" className="block text-sm font-medium text-slate-200 mb-2">
                Confirm Password
              </label>
              <div className="mt-1 relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Lock className="h-5 w-5 text-slate-400" />
                </div>
                <input
                  id="confirmPassword"
                  name="confirmPassword"
                  type={showConfirmPassword ? 'text' : 'password'}
                  autoComplete="new-password"
                  required
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  className="appearance-none block w-full pl-10 pr-10 py-3 glass border border-white/20 rounded-xl placeholder-slate-400 text-slate-200 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm transition-all duration-200"
                  placeholder="Confirm your password"
                  disabled={loading}
                />
                <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
                  <button
                    type="button"
                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    className="text-slate-400 hover:text-slate-200 transition-colors duration-200"
                    disabled={loading}
                  >
                    {showConfirmPassword ? (
                      <EyeOff className="h-5 w-5" />
                    ) : (
                      <Eye className="h-5 w-5" />
                    )}
                  </button>
                </div>
              </div>
            </div>

            {/* Terms and Conditions */}
            <div className="flex items-start">
              <input
                id="acceptedTerms"
                name="acceptedTerms"
                type="checkbox"
                checked={acceptedTerms}
                onChange={(e) => setAcceptedTerms(e.target.checked)}
                className="h-4 w-4 text-primary-500 focus:ring-primary-500 border-white/30 rounded bg-white/10 mt-1"
                disabled={loading}
              />
              <label htmlFor="acceptedTerms" className="ml-2 block text-sm text-slate-200">
                I agree to the{' '}
                <button type="button" className="font-medium text-primary-400 hover:text-primary-300 transition-colors duration-200">
                  Terms of Service
                </button>{' '}
                and{' '}
                <button type="button" className="font-medium text-primary-400 hover:text-primary-300 transition-colors duration-200">
                  Privacy Policy
                </button>
              </label>
            </div>

            {/* Error Message */}
            {error && (
              <div className="glass bg-red-500/10 border border-red-400/30 rounded-xl p-4 backdrop-blur-sm">
                <div className="flex">
                  <AlertCircle className="h-5 w-5 text-red-300" />
                  <div className="ml-3">
                    <h3 className="text-sm font-medium text-red-200">Registration Failed</h3>
                    <div className="mt-2 text-sm text-red-300">{error}</div>
                  </div>
                </div>
              </div>
            )}

            {/* Submit Button */}
            <div>
              <button
                type="submit"
                disabled={loading}
                className="w-full flex justify-center items-center py-3 px-4 border border-transparent rounded-xl shadow-crypto text-sm font-medium text-white btn-crypto-primary focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200"
              >
                {loading ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Creating Account...
                  </>
                ) : (
                  'Create Account'
                )}
              </button>
            </div>
          </form>

          {/* Security Notice */}
          <div className="mt-6">
            <div className="glass bg-primary-500/10 border border-primary-400/30 rounded-xl p-4 backdrop-blur-sm">
              <div className="flex">
                <CheckCircle className="h-5 w-5 text-primary-300" />
                <div className="ml-3">
                  <h3 className="text-sm font-medium text-primary-200">Secure Registration</h3>
                  <div className="mt-1 text-sm text-primary-300">
                    Your information is encrypted and protected. MSME accounts will require KYC verification before creating DROs.
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

