export default DRODetailModal;

import React, { useState, useEffect } from 'react';
import type { DRO } from '../services/api';
import { droAPI, revenueAPI } from '../services/api';
import { X, DollarSign, TrendingUp, Calculator, AlertTriangle, CheckCircle } from 'lucide-react';

interface InvestmentModalProps {
  dro: DRO;
  onClose: () => void;
  onSuccess: () => void;
}

const InvestmentModal: React.FC<InvestmentModalProps> = ({ dro, onClose, onSuccess }) => {
  const [investmentAmount, setInvestmentAmount] = useState('');
  const [projectedReturns, setProjectedReturns] = useState<any>(null);
  const [loading, setLoading] = useState(false);
  const [calculating, setCalculating] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatPercentage = (value: number) => {
    return `${value.toFixed(2)}%`;
  };

  // Calculate projected returns when investment amount changes
  useEffect(() => {
    const calculateReturns = async () => {
      if (!investmentAmount || parseFloat(investmentAmount) <= 0) {
        setProjectedReturns(null);
        return;
      }

      try {
        setCalculating(true);
        const response = await revenueAPI.calculateReturns({
          dro_id: dro.id,
          investment_amount: parseFloat(investmentAmount),
          projected_monthly_revenue: 50000, // Default assumption
          duration_months: dro.duration_months,
        });
        setProjectedReturns(response.projected_returns);
      } catch (err) {
        console.error('Error calculating returns:', err);
        setProjectedReturns(null);
      } finally {
        setCalculating(false);
      }
    };

    const debounceTimer = setTimeout(calculateReturns, 500);
    return () => clearTimeout(debounceTimer);
  }, [investmentAmount, dro.id, dro.duration_months]);

  const handleInvest = async () => {
    if (!investmentAmount || parseFloat(investmentAmount) <= 0) {
      setError('Please enter a valid investment amount');
      return;
    }

    if (parseFloat(investmentAmount) < dro.minimum_investment) {
      setError(`Minimum investment is ${formatCurrency(dro.minimum_investment)}`);
      return;
    }

    const remainingTarget = dro.target_amount - (dro.raised_amount || 0);
    if (parseFloat(investmentAmount) > remainingTarget) {
      setError(`Investment amount exceeds remaining target of ${formatCurrency(remainingTarget)}`);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      await droAPI.investInDRO(dro.id, parseFloat(investmentAmount));
      setSuccess(true);

      // Show success message for 2 seconds, then close
      setTimeout(() => {
        onSuccess();
      }, 2000);

    } catch (err: any) {
      console.error('Investment error:', err);
      const errorMessage = err.response?.data?.error || 'Investment failed. Please try again.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const validationErrors = [];
  if (investmentAmount && parseFloat(investmentAmount) < dro.minimum_investment) {
    validationErrors.push(`Minimum investment: ${formatCurrency(dro.minimum_investment)}`);
  }
  if (investmentAmount && parseFloat(investmentAmount) > (dro.target_amount - (dro.raised_amount || 0))) {
    validationErrors.push(`Exceeds remaining target: ${formatCurrency(dro.target_amount - (dro.raised_amount || 0))}`);
  }

  if (success) {
    return (
      <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-50">
        <div className="glass-card max-w-md w-full p-6 text-center shadow-crypto-lg border border-white/10">
          <CheckCircle className="w-16 h-16 text-accent-green mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-white mb-2">Investment Successful!</h2>
          <p className="text-slate-300 mb-4">
            You've successfully invested {formatCurrency(parseFloat(investmentAmount))} in {dro.title}.
          </p>
          <p className="text-sm text-slate-400">
            You'll start receiving revenue distributions once the MSME reports their daily revenue.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-50">
      <div className="glass-card max-w-2xl w-full max-h-[90vh] overflow-y-auto shadow-crypto-lg border border-white/10">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-white/10">
          <div>
            <h2 className="text-xl font-semibold text-white">Invest in DRO</h2>
            <p className="text-sm text-slate-300">{dro.title}</p>
          </div>
          <button
            onClick={onClose}
            className="text-slate-400 hover:text-white transition-colors duration-200"
            disabled={loading}
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="p-6">
          {/* DRO Summary */}
          <div className="glass rounded-xl p-4 mb-6 border border-white/10 backdrop-blur-sm">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <div className="text-sm text-slate-400">Revenue Share</div>
                <div className="font-semibold text-primary-400">
                  {formatPercentage(dro.revenue_percentage)}
                </div>
              </div>
              <div>
                <div className="text-sm text-slate-400">Duration</div>
                <div className="font-semibold text-white">{dro.duration_months} months</div>
              </div>
              <div>
                <div className="text-sm text-slate-400">Min Investment</div>
                <div className="font-semibold text-white">{formatCurrency(dro.minimum_investment)}</div>
              </div>
              <div>
                <div className="text-sm text-slate-400">Remaining Target</div>
                <div className="font-semibold text-white">
                  {formatCurrency(dro.target_amount - (dro.raised_amount || 0))}
                </div>
              </div>
            </div>
          </div>

          {/* Investment Amount Input */}
          <div className="mb-6">
            <label className="block text-sm font-medium text-slate-200 mb-2">
              Investment Amount
            </label>
            <div className="relative">
              <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400 w-5 h-5" />
              <input
                type="number"
                value={investmentAmount}
                onChange={(e) => setInvestmentAmount(e.target.value)}
                placeholder={`Min: ${dro.minimum_investment.toLocaleString()}`}
                className="w-full pl-10 pr-4 py-3 glass border border-white/20 rounded-xl focus:ring-2 focus:ring-primary-500 focus:border-primary-500 text-lg text-slate-200 placeholder-slate-400 backdrop-blur-sm transition-all duration-200"
                disabled={loading}
              />
            </div>

            {/* Quick Amount Buttons */}
            <div className="mt-3 flex flex-wrap gap-2">
              {[dro.minimum_investment, 5000, 10000, 25000].map((amount) => (
                <button
                  key={amount}
                  onClick={() => setInvestmentAmount(amount.toString())}
                  className="px-3 py-2 text-sm glass border border-white/20 rounded-lg hover:bg-white/10 text-slate-300 hover:text-white transition-all duration-200 backdrop-blur-sm"
                  disabled={loading || amount > (dro.target_amount - (dro.raised_amount || 0))}
                >
                  {formatCurrency(amount)}
                </button>
              ))}
            </div>

            {/* Validation Errors */}
            {validationErrors.length > 0 && (
              <div className="mt-2 flex items-start text-red-300 text-sm">
                <AlertTriangle className="w-4 h-4 mr-1 mt-0.5 flex-shrink-0" />
                <ul>
                  {validationErrors.map((error, index) => (
                    <li key={index}>{error}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          {/* Projected Returns */}
          {investmentAmount && parseFloat(investmentAmount) > 0 && (
            <div className="mb-6">
              <div className="flex items-center mb-3">
                <Calculator className="w-5 h-5 text-slate-400 mr-2" />
                <h3 className="text-lg font-medium text-white">Projected Returns</h3>
                {calculating && (
                  <div className="ml-2 text-sm text-slate-400">Calculating...</div>
                )}
              </div>

              {projectedReturns && !calculating ? (
                <div className="glass bg-primary-500/10 border border-primary-500/30 rounded-xl p-4 backdrop-blur-sm">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <div className="text-sm text-slate-400">Ownership Percentage</div>
                      <div className="text-lg font-semibold text-primary-400">
                        {formatPercentage(projectedReturns.ownership_percentage)}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-slate-400">Annual Return Rate</div>
                      <div className="text-lg font-semibold text-primary-400">
                        {formatPercentage(projectedReturns.annual_return_rate)}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-slate-400">Monthly Average</div>
                      <div className="text-lg font-semibold text-white">
                        {formatCurrency(projectedReturns.monthly_average_return)}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-slate-400">Total Potential Return</div>
                      <div className="text-lg font-semibold text-accent-green">
                        {formatCurrency(projectedReturns.total_potential_return)}
                      </div>
                    </div>
                  </div>

                  <div className="mt-3 text-xs text-slate-400">
                    * Projections based on estimated monthly revenue. Actual returns may vary.
                  </div>
                </div>
              ) : calculating ? (
                <div className="glass rounded-xl p-4 animate-pulse backdrop-blur-sm">
                  <div className="h-4 bg-white/20 rounded mb-2"></div>
                  <div className="h-4 bg-white/20 rounded w-3/4"></div>
                </div>
              ) : null}
            </div>
          )}

          {/* Risk Disclosure */}
          <div className="mb-6 glass bg-accent-gold/10 border border-accent-gold/30 rounded-xl p-4 backdrop-blur-sm">
            <div className="flex items-start">
              <AlertTriangle className="w-5 h-5 text-accent-gold mr-2 mt-0.5 flex-shrink-0" />
              <div className="text-sm">
                <div className="font-medium text-accent-gold mb-1">Investment Risk Disclosure</div>
                <ul className="text-slate-300 space-y-1">
                  <li>â€¢ Revenue-based investments carry risk and returns are not guaranteed</li>
                  <li>â€¢ Actual returns depend on the business's daily revenue performance</li>
                  <li>â€¢ You may lose some or all of your investment</li>
                  <li>â€¢ Please consider your risk tolerance before investing</li>
                </ul>
              </div>
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <div className="mb-4 glass bg-red-500/10 border border-red-400/30 rounded-xl p-4 backdrop-blur-sm">
              <div className="flex items-start">
                <AlertTriangle className="w-5 h-5 text-red-300 mr-2 mt-0.5 flex-shrink-0" />
                <div className="text-sm text-red-300">{error}</div>
              </div>
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex space-x-4">
            <button
              onClick={onClose}
              className="flex-1 btn-crypto-secondary"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              onClick={handleInvest}
              disabled={
                loading || 
                !investmentAmount || 
                parseFloat(investmentAmount) <= 0 || 
                validationErrors.length > 0
              }
              className="flex-1 btn-crypto-primary flex items-center justify-center"
            >
              {loading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Processing...
                </>
              ) : (
                <>
                  <TrendingUp className="w-4 h-4 mr-2" />
                  Invest {investmentAmount ? formatCurrency(parseFloat(investmentAmount)) : ''}
                </>
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InvestmentModal;

