import axios from 'axios';

// Use environment variable for API URL, fallback to production API
const API_BASE_URL = import.meta.env.VITE_API_URL 
  ? `${import.meta.env.VITE_API_URL}/api`
  : 'https://sirex-api.onrender.com/api';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('access_token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle auth errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('access_token');
      localStorage.removeItem('refresh_token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Types
export interface User {
  id: number;
  email: string;
  name: string;
  first_name?: string;
  last_name?: string;
  user_type: 'investor' | 'msme';
  kyc_status: 'pending' | 'approved' | 'rejected';
  is_verified: boolean;
}

export interface DRO {
  id: number;
  msme_id: number;
  title: string;
  description?: string;
  industry: string;
  revenue_percentage: number;
  minimum_investment: number;
  target_amount: number;
  raised_amount: number;
  duration_months: number;
  status: 'pending' | 'active' | 'completed' | 'cancelled';
  created_at: string;
  msme_name?: string;
  funding_percentage?: number;
  total_invested?: number;
  investor_count?: number;
}

export interface Investment {
  id: number;
  investor_id: number;
  dro_id: number;
  amount: number;
  investment_date: string;
  status: 'active' | 'completed' | 'cancelled';
  dro_title?: string;
  dro_industry?: string;
  dro_revenue_percentage?: number;
  investor_name?: string;
}

export interface AuthTokens {
  access_token: string;
  refresh_token: string;
  token_type: string;
}

export interface AuthResponse {
  message: string;
  user: User;
  tokens: AuthTokens;
}

// Auth API
export const authAPI = {
  register: async (userData: {
    email: string;
    password: string;
    name?: string;
    first_name?: string;
    last_name?: string;
    user_type: 'investor' | 'msme';
    company_name?: string;
  }): Promise<AuthResponse> => {
    const response = await api.post('/auth/register', userData);
    return response.data;
  },

  login: async (credentials: {
    email: string;
    password: string;
  }): Promise<AuthResponse> => {
    const response = await api.post('/auth/login', credentials);
    return response.data;
  },

  logout: async (): Promise<void> => {
    await api.post('/auth/logout');
  },

  getCurrentUser: async (): Promise<{ user: User }> => {
    const response = await api.get('/auth/me');
    return response.data;
  },

  refreshToken: async (refreshToken: string): Promise<AuthTokens> => {
    const response = await api.post('/auth/refresh', {
      refresh_token: refreshToken,
    });
    return response.data.tokens;
  },
};

// DRO API
export const droAPI = {
  getDROs: async (filters?: {
    industry?: string;
    min_investment?: number;
    max_investment?: number;
    min_return?: number;
    max_return?: number;
    status?: string;
    search?: string;
    page?: number;
    limit?: number;
  }): Promise<{
    dros: DRO[];
    pagination: { page: number; limit: number; total: number };
    filters: any;
  }> => {
    const response = await api.get('/dro', { params: filters });
    return response.data;
  },

  getDRO: async (id: number): Promise<{
    dro: DRO;
    investments: Investment[];
  }> => {
    const response = await api.get(`/dro/${id}`);
    return response.data;
  },

  createDRO: async (droData: {
    title: string;
    description?: string;
    industry: string;
    revenue_percentage: number;
    minimum_investment: number;
    target_amount: number;
    duration_months: number;
  }): Promise<{ message: string; dro: DRO }> => {
    const response = await api.post('/dro', droData);
    return response.data;
  },

  investInDRO: async (droId: number, amount: number): Promise<{
    message: string;
    investment: Investment;
  }> => {
    const response = await api.post(`/dro/${droId}/invest`, { amount });
    return response.data;
  },

  getMyDROs: async (): Promise<{ dros: DRO[]; total: number }> => {
    const response = await api.get('/dro/my-dros');
    return response.data;
  },

  getMyInvestments: async (): Promise<{
    total_invested: number;
    active_investments: number;
    total_return_earned: number;
    investments: Investment[];
  }> => {
    const response = await api.get('/dro/my-investments');
    return response.data;
  },

  getStatistics: async (): Promise<{
    statistics: {
      total_dros: number;
      active_dros: number;
      total_funding: number;
      completed_dros: number;
    };
    industry_breakdown: Array<{
      industry: string;
      count: number;
      total_funding: number;
    }>;
  }> => {
    const response = await api.get('/dro/statistics');
    return response.data;
  },
};

// Revenue API
export const revenueAPI = {
  getMyPayouts: async (page?: number, limit?: number): Promise<{
    payouts: any[];
    pagination: { page: number; limit: number; total: number };
    summary: {
      total_payouts: number;
      total_amount: number;
      pending_amount: number;
      completed_amount: number;
    };
  }> => {
    const response = await api.get('/revenue/my-payouts', {
      params: { page, limit },
    });
    return response.data;
  },

  getPerformance: async (): Promise<{
    performance: {
      total_invested: number;
      total_returns: number;
      net_profit: number;
      roi_percentage: number;
      active_investments: number;
      completed_payouts: number;
      pending_payouts: number;
      average_monthly_return: number;
    };
  }> => {
    const response = await api.get('/revenue/performance');
    return response.data;
  },

  calculateReturns: async (data: {
    dro_id: number;
    investment_amount: number;
    projected_monthly_revenue: number;
    duration_months: number;
  }): Promise<{
    dro: { id: number; title: string; industry: string };
    investment_amount: number;
    projected_returns: {
      total_potential_return: number;
      monthly_average_return: number;
      annual_return_rate: number;
      ownership_percentage: number;
    };
  }> => {
    const response = await api.post('/revenue/calculate-returns', data);
    return response.data;
  },
};

// Helper functions
export const setAuthTokens = (tokens: AuthTokens) => {
  localStorage.setItem('access_token', tokens.access_token);
  localStorage.setItem('refresh_token', tokens.refresh_token);
};

export const clearAuthTokens = () => {
  localStorage.removeItem('access_token');
  localStorage.removeItem('refresh_token');
  localStorage.removeItem('user');
};

export const clearAuth = () => {
  localStorage.removeItem('access_token');
  localStorage.removeItem('refresh_token');
  localStorage.removeItem('user');
};

export const getCurrentUser = (): User | null => {
  const userStr = localStorage.getItem('user');
  return userStr ? JSON.parse(userStr) : null;
};

export const setCurrentUser = (user: User) => {
  localStorage.setItem('user', JSON.stringify(user));
};

export default api;

