import React, { useState, useEffect } from 'react';
import type { DRO } from '../services/api';
import { droAPI } from '../services/api';
import { 
  Plus,
  TrendingUp, 
  DollarSign, 
  Target, 
  Users,
  BarChart3,
  Calendar,
  Edit,
  Eye,
  AlertCircle,
  RefreshCw,
  Download
} from 'lucide-react';

interface MSMEStats {
  total_dros: number;
  active_dros: number;
  total_funding: number;
  completed_dros: number;
}

const MSMEDashboard: React.FC = () => {
  const [dros, setDROs] = useState<DRO[]>([]);
  const [, setStats] = useState<MSMEStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'overview' | 'dros' | 'revenue'>('overview');

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
