  const getFundingPercentage = (dro: DRO) => {
    return dro.target_amount > 0 ? ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
