                <div className="flex justify-between py-2 border-b border-white/10">
                  <span className="text-slate-400">Duration</span>
                  <span className="font-medium text-white">{displayDRO.duration_months} months</span>
                </div>
                <div className="flex justify-between py-2 border-b border-white/10">
                  <span className="text-slate-400">Created</span>
                  <span className="font-medium text-white">{formatDate(displayDRO.created_at)}</span>
                </div>
              </div>
            </div>

            {/* Recent Investors */}
            <div>
              <h3 className="text-lg font-semibold text-white mb-4 flex items-center">
                <BarChart3 className="w-5 h-5 mr-2" />
                Recent Investors
              </h3>
              {loading ? (
                <div className="space-y-3">
                  {[1, 2, 3].map((i) => (
                    <div key={i} className="animate-pulse">
                      <div className="h-4 bg-white/20 rounded mb-2"></div>
                      <div className="h-3 bg-white/20 rounded w-3/4"></div>
                    </div>
                  ))}
                </div>
              ) : investments.length > 0 ? (
                <div className="space-y-3">
                  {investments.slice(0, 5).map((investment, index) => (
                    <div key={index} className="flex justify-between items-center py-2 border-b border-white/10">
                      <div>
                        <div className="font-medium text-white">
                          {investment.investor_name || 'Anonymous Investor'}
                        </div>
                        <div className="text-sm text-slate-400">
                          {formatDate(investment.investment_date)}
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="font-medium text-primary-400">
                          {formatCurrency(investment.amount)}
                        </div>
                        <div className="text-xs text-slate-400 capitalize">
                          {investment.status}
                        </div>
                      </div>
                    </div>
                  ))}
                  {investments.length > 5 && (
                    <div className="text-center text-sm text-slate-400 pt-2">
                      +{investments.length - 5} more investors
                    </div>
                  )}
                </div>
              ) : (
                <div className="text-center py-8 text-slate-400">
                  <Users className="w-12 h-12 mx-auto mb-2 text-slate-500" />
                  <p>No investors yet</p>
                  <p className="text-sm">Be the first to invest!</p>
                </div>
              )}
            </div>
          </div>

          {/* Risk Notice */}
          <div className="glass bg-accent-gold/10 border border-accent-gold/30 rounded-xl p-4 mb-6 backdrop-blur-sm">
            <div className="flex items-start">
              <div className="text-accent-gold mr-3 mt-1">âš ï¸</div>
              <div>
                <h4 className="font-medium text-accent-gold mb-1">Investment Risk Notice</h4>
                <p className="text-sm text-slate-300">
                  Revenue-based investments carry inherent risks. Returns are not guaranteed and depend on the business's 
                  actual revenue performance. You should carefully consider your risk tolerance and investment objectives 
                  before investing.
                </p>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex space-x-4">
            <button
              onClick={onClose}
              className="flex-1 btn-crypto-secondary btn-md"
            >
              Close
            </button>

            {displayDRO.status === 'active' && (
              <button
                onClick={() => onInvest(displayDRO)}
                className="flex-1 btn-crypto-primary btn-md flex items-center justify-center"
              >
                <Target className="w-5 h-5 mr-2" />
                Invest Now
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DRODetailModal;

