import React, { useState, useEffect } from 'react';
import type { DRO } from '../services/api';
import { droAPI, revenueAPI } from '../services/api';
import { X, DollarSign, TrendingUp, Calculator, AlertTriangle, CheckCircle } from 'lucide-react';

interface InvestmentModalProps {
  dro: DRO;
  onClose: () => void;
  onSuccess: () => void;
}

const InvestmentModal: React.FC<InvestmentModalProps> = ({ dro, onClose, onSuccess }) => {
  const [investmentAmount, setInvestmentAmount] = useState('');
  const [projectedReturns, setProjectedReturns] = useState<any>(null);
  const [loading, setLoading] = useState(false);
  const [calculating, setCalculating] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatPercentage = (value: number) => {
    return `${value.toFixed(2)}%`;
  };

  // Calculate projected returns when investment amount changes
  useEffect(() => {
    const calculateReturns = async () => {
      if (!investmentAmount || parseFloat(investmentAmount) <= 0) {
        setProjectedReturns(null);
        return;
      }

      try {
        setCalculating(true);
        const response = await revenueAPI.calculateReturns({
          dro_id: dro.id,
          investment_amount: parseFloat(investmentAmount),
          projected_monthly_revenue: 50000, // Default assumption
          duration_months: dro.duration_months,
        });
        setProjectedReturns(response.projected_returns);
      } catch (err) {
        console.error('Error calculating returns:', err);
        setProjectedReturns(null);
      } finally {
        setCalculating(false);
      }
    };

    const debounceTimer = setTimeout(calculateReturns, 500);
    return () => clearTimeout(debounceTimer);
  }, [investmentAmount, dro.id, dro.duration_months]);

  const handleInvest = async () => {
    if (!investmentAmount || parseFloat(investmentAmount) <= 0) {
      setError('Please enter a valid investment amount');
      return;
    }

    if (parseFloat(investmentAmount) < dro.minimum_investment) {
      setError(`Minimum investment is ${formatCurrency(dro.minimum_investment)}`);
      return;
    }

    const remainingTarget = dro.target_amount - (dro.raised_amount || 0);
    if (parseFloat(investmentAmount) > remainingTarget) {
      setError(`Investment amount exceeds remaining target of ${formatCurrency(remainingTarget)}`);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      await droAPI.investInDRO(dro.id, parseFloat(investmentAmount));
      setSuccess(true);

      // Show success message for 2 seconds, then close
      setTimeout(() => {
        onSuccess();
      }, 2000);

    } catch (err: any) {
      console.error('Investment error:', err);
      const errorMessage = err.response?.data?.error || 'Investment failed. Please try again.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const validationErrors = [];

