import React, { useState } from 'react';
import { Search, X, TrendingUp, Building2, DollarSign, Zap, Shield } from 'lucide-react';

interface FilterValues {
  search: string;
  industry: string;
  min_investment: string;
  max_investment: string;
  min_return: string;
  max_return: string;
  status: string;
}

interface DROFiltersProps {
  onFiltersChange: (filters: FilterValues) => void;
  isLoading?: boolean;
}

const DROFilters: React.FC<DROFiltersProps> = ({ onFiltersChange, isLoading = false }) => {
  const [filters, setFilters] = useState<FilterValues>({
    search: '',
    industry: '',
    min_investment: '1000',
    max_investment: '50000',
    min_return: '5',
    max_return: '20',
    status: 'active',
  });


  const industries = [
    'Technology',
    'Food & Beverage',
    'Retail',
    'Healthcare',
    'Education',
    'Manufacturing',
    'Services',
    'Agriculture',
    'Real Estate',
    'Entertainment',
    'Other',
  ];

  const statuses = [
    { value: '', label: 'All Status' },
    { value: 'active', label: 'Active' },
    { value: 'pending', label: 'Pending' },
    { value: 'completed', label: 'Completed' },
  ];

