import axios from 'axios';

// Use environment variable for API URL, fallback to production API
const API_BASE_URL = import.meta.env.VITE_API_URL 
  ? `${import.meta.env.VITE_API_URL}/api`
  : 'https://sirex-api.onrender.com/api';

// Performance Optimization: API Caching System
interface CacheEntry {
  data: any;
  timestamp: number;
  expiry: number;
}

class APICache {
  private cache = new Map<string, CacheEntry>();
  private inflightRequests = new Map<string, Promise<any>>();

  get(key: string): any | null {
    const entry = this.cache.get(key);
    if (!entry) return null;

    if (Date.now() > entry.expiry) {
      this.cache.delete(key);
      return null;
    }

    return entry.data;
  }

  set(key: string, data: any, ttlMs: number = 5 * 60 * 1000): void {
    this.cache.set(key, {
      data,
      timestamp: Date.now(),
      expiry: Date.now() + ttlMs
    });
  }

  invalidate(pattern?: string): void {
    if (!pattern) {
      this.cache.clear();
      return;
    }

    for (const key of this.cache.keys()) {
      if (key.includes(pattern)) {
        this.cache.delete(key);
      }
    }
  }

  async withCache<T>(
    key: string, 
    apiCall: () => Promise<T>, 
    ttlMs: number = 5 * 60 * 1000
  ): Promise<T> {
    // Check cache first
    const cached = this.get(key);
    if (cached) return cached;

    // Check if request is already in flight
    const inflightRequest = this.inflightRequests.get(key);
    if (inflightRequest) return inflightRequest;

    // Make the API call
    const request = apiCall().finally(() => {
      this.inflightRequests.delete(key);
    });

    this.inflightRequests.set(key, request);

    try {
      const data = await request;
      this.set(key, data, ttlMs);
      return data;
    } catch (error) {
      this.inflightRequests.delete(key);
      throw error;
    }
  }
}

