import React from 'react';

interface SkeletonLoaderProps {
  type: 'card' | 'text' | 'avatar' | 'button' | 'stat' | 'chart';
  count?: number;
  className?: string;
}

const SkeletonLoader: React.FC<SkeletonLoaderProps> = ({ type, count = 1, className = '' }) => {
  const skeletons = Array.from({ length: count }, (_, index) => {
    switch (type) {
      case 'card':
        return (
          <div key={index} className={`glass-card p-6 space-y-4 animate-pulse ${className}`}>
            {/* Header */}
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 bg-white/20 rounded-xl"></div>
              <div className="flex-1 space-y-2">
                <div className="h-5 bg-white/20 rounded w-3/4"></div>
                <div className="h-4 bg-white/20 rounded w-1/2"></div>
              </div>
              <div className="w-16 h-6 bg-white/20 rounded-full"></div>
            </div>

            {/* Description */}
            <div className="space-y-2">
              <div className="h-4 bg-white/20 rounded w-full"></div>
              <div className="h-4 bg-white/20 rounded w-2/3"></div>
            </div>

            {/* Metrics */}
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <div className="h-8 bg-white/20 rounded"></div>
                <div className="h-3 bg-white/20 rounded w-3/4"></div>
              </div>
              <div className="space-y-2">
                <div className="h-8 bg-white/20 rounded"></div>
                <div className="h-3 bg-white/20 rounded w-3/4"></div>
              </div>
            </div>

            {/* Progress bar */}
            <div className="space-y-2">
              <div className="h-2 bg-white/20 rounded-full"></div>
              <div className="flex justify-between">
                <div className="h-3 bg-white/20 rounded w-16"></div>
                <div className="h-3 bg-white/20 rounded w-16"></div>
              </div>
            </div>

            {/* Buttons */}
            <div className="flex space-x-3">
              <div className="flex-1 h-12 bg-white/20 rounded-xl"></div>
              <div className="flex-1 h-12 bg-white/20 rounded-xl"></div>
            </div>
          </div>
        );

      case 'text':
        return (
          <div key={index} className={`space-y-2 animate-pulse ${className}`}>
            <div className="h-4 bg-white/20 rounded w-full"></div>
            <div className="h-4 bg-white/20 rounded w-5/6"></div>
            <div className="h-4 bg-white/20 rounded w-4/6"></div>
          </div>
        );

      case 'avatar':
        return (
          <div key={index} className={`w-10 h-10 bg-white/20 rounded-full animate-pulse ${className}`}></div>
        );

      case 'button':
        return (
          <div key={index} className={`h-10 bg-white/20 rounded-lg animate-pulse ${className}`}></div>
        );

      case 'stat':
        return (
          <div key={index} className={`glass rounded-xl p-4 space-y-3 animate-pulse ${className}`}>
            <div className="flex items-center justify-between">
              <div className="w-6 h-6 bg-white/20 rounded"></div>
              <div className="h-8 bg-white/20 rounded w-16"></div>
            </div>
            <div className="h-3 bg-white/20 rounded w-20"></div>
          </div>
        );

      case 'chart':
        return (
          <div key={index} className={`glass rounded-xl p-4 space-y-4 animate-pulse ${className}`}>
            <div className="h-5 bg-white/20 rounded w-32"></div>
            <div className="h-40 bg-white/20 rounded"></div>
            <div className="flex justify-between">
              <div className="h-3 bg-white/20 rounded w-16"></div>
              <div className="h-3 bg-white/20 rounded w-16"></div>
              <div className="h-3 bg-white/20 rounded w-16"></div>
            </div>
          </div>
        );

      default:
        return (
          <div key={index} className={`h-20 bg-white/20 rounded animate-pulse ${className}`}></div>
        );
    }
  });

  return <>{skeletons}</>;
};

export default SkeletonLoader;

