import React, { useState, useEffect } from 'react';
import type { DRO } from '../services/api';
import { droAPI, cacheControl } from '../services/api';
import VCTooltip from './VCTooltip';
import DROCard from './DROCard';
import DROFilters from './DROFilters';
import InvestmentModal from './InvestmentModal';
import DRODetailModal from './DRODetailModal';
import SkeletonLoader from './SkeletonLoader';
import { Loader2, AlertCircle, ChevronLeft, ChevronRight, Target } from 'lucide-react';

interface FilterValues {
  search: string;
  industry: string;
  min_investment: string;
  max_investment: string;
  min_return: string;
  max_return: string;
  status: string;
}

interface DROListProps {
  onNavigateToRecommended?: () => void;
}

const DROList: React.FC<DROListProps> = ({ onNavigateToRecommended }) => {
  const [dros, setDros] = useState<DRO[]>([]);
  const [loading, setLoading] = useState(true);
  const [initialLoad, setInitialLoad] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedDRO, setSelectedDRO] = useState<DRO | null>(null);
  const [showInvestmentModal, setShowInvestmentModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [filters, setFilters] = useState<FilterValues>({
    search: '',
    industry: '',
    min_investment: '',
    max_investment: '',
    min_return: '',
    max_return: '',
    status: 'active',
  });
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 12,
    total: 0,
  });

  // Progressive data fetching with optimizations
  const fetchDROs = async (showLoadingSpinner = true) => {
    try {
      if (showLoadingSpinner) {
        setLoading(true);
      }
      setError(null);

      // Convert filter values to API format
      const apiFilters: any = {
        page: pagination.page,
        limit: pagination.limit,
      };

      if (filters.search) apiFilters.search = filters.search;
      if (filters.industry) apiFilters.industry = filters.industry;
      if (filters.status) apiFilters.status = filters.status;
      if (filters.min_investment) apiFilters.min_investment = parseFloat(filters.min_investment);
      if (filters.max_investment) apiFilters.max_investment = parseFloat(filters.max_investment);
      if (filters.min_return) apiFilters.min_return = parseFloat(filters.min_return);
      if (filters.max_return) apiFilters.max_return = parseFloat(filters.max_return);

      const response = await droAPI.getDROs(apiFilters);

      setDros(response.dros);
      setPagination(prev => ({ ...prev, total: response.pagination?.total || response.dros.length }));

      // Preload next page for better UX
      if (response.pagination?.page && response.pagination.page * response.pagination.limit < response.pagination.total) {
        const nextPageFilters = { ...apiFilters, page: response.pagination.page + 1 };
        cacheControl.preload(`dros:${JSON.stringify(nextPageFilters)}`, () => droAPI.getDROs(nextPageFilters));
      }

      setInitialLoad(false);
    } catch (err) {
      console.error('Error fetching DROs:', err);

      // Check if it's a CORS error
      if (err instanceof Error && (err.message.includes('CORS') || err.message.includes('Network Error'))) {
        setError('ðŸ”§ APIé€£æŽ¥éŒ¯èª¤ï¼šè«‹æª¢æŸ¥å¾Œç«¯CORSè¨­å®šæ˜¯å¦å…è¨± sirex-frontend.onrender.com è¨ªå•');
      } else {
        setError('è¼‰å…¥æŠ•è³‡æ©Ÿæœƒå¤±æ•—ï¼Œè«‹ç¨å¾Œå†è©¦ã€‚');
      }
    } finally {
      if (showLoadingSpinner) {
        setLoading(false);
      }
    }
  };

  // Effect to fetch DROs when filters or pagination change
  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      // Only show loading spinner on initial load or search changes
      const showSpinner = initialLoad || filters.search !== '';
      fetchDROs(showSpinner);
    }, filters.search ? 800 : 100); // Longer debounce for search, shorter for filters

    return () => clearTimeout(debounceTimer);
  }, [filters, pagination.page]);

  // Handle filter changes
  const handleFiltersChange = (newFilters: FilterValues) => {
    setFilters(newFilters);
    setPagination(prev => ({ ...prev, page: 1 })); // Reset to first page
  };

  // Handle investment
  const handleInvest = (dro: DRO) => {
    setSelectedDRO(dro);
    setShowInvestmentModal(true);
  };

  // Handle view details
  const handleViewDetails = (dro: DRO) => {
    setSelectedDRO(dro);
    setShowDetailModal(true);
  };

  // Handle successful investment
  const handleInvestmentSuccess = () => {
    setShowInvestmentModal(false);
    setSelectedDRO(null);
    fetchDROs(); // Refresh the list
  };

  // Handle pagination
  const handlePageChange = (newPage: number) => {
    if (newPage >= 1 && newPage <= Math.ceil(pagination.total / pagination.limit)) {
      setPagination(prev => ({ ...prev, page: newPage }));
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  // Calculate pagination info
  const totalPages = Math.ceil(pagination.total / pagination.limit);

  // Progressive loading: Show skeleton screens instead of full page loading
  const showSkeletons = initialLoad && dros.length === 0;

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-dark flex items-center justify-center">
        <div className="text-center max-w-md mx-auto">
          <div className="glass-card p-12">
            <AlertCircle className="w-16 h-16 text-red-500 mx-auto mb-6" />
            <h2 className="text-2xl font-bold text-white mb-4">Error Loading DROs</h2>
            <p className="text-slate-300 text-lg mb-8">{error}</p>
            <button
              onClick={fetchDROs}
              className="btn-crypto-primary w-full"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-dark">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Hero Header */}
        <div className="text-center mb-12">
          <div className="flex items-center justify-center mb-4">
            <h1 className="text-5xl font-bold text-white tracking-tight">
              Browse <span className="text-gradient-primary">All DROs</span>
            </h1>
            <div className="ml-3">
              <VCTooltip 
                title="Complete DRO Marketplace"
                content="Our full marketplace showcases the breadth of investment opportunities across industries and geographies. This comprehensive view demonstrates our platform's ability to serve diverse investor preferences and risk profiles, crucial for scaling to enterprise clients."
              />
            </div>
          </div>
          <p className="text-xl text-slate-300 max-w-3xl mx-auto leading-relaxed mb-6">
            Explore our complete collection of Daily Revenue Obligations from verified businesses worldwide
          </p>

          {/* Prominent Navigation Buttons */}
          <div className="flex flex-col sm:flex-row items-center justify-center gap-4 mb-8">
            <div className="text-slate-300 text-lg font-medium">Choose your browsing style:</div>
            <div className="flex items-center gap-4">
              <button
                onClick={() => onNavigateToRecommended?.()}
                className="btn-crypto-primary text-lg px-8 py-4 flex items-center space-x-3 shadow-crypto-lg glow-primary"
              >
                <Target className="w-6 h-6" />
                <span>ðŸŽ¯ Get Recommendations</span>
                <div className="w-2 h-2 bg-accent-gold rounded-full animate-pulse"></div>
              </button>
              <div className="text-slate-400 text-lg font-medium">or</div>
              <div className="glass-card px-6 py-3 border-2 border-primary-500/30">
                <span className="text-white font-bold text-lg">ðŸ“‹ Browse All DROs</span>
                <div className="text-sm text-slate-300 mt-1">Current page - Use filters below</div>
              </div>
            </div>
          </div>
        </div>

        {/* Featured Stats */}
        <div className="mb-12">
          <div className="flex items-center justify-center mb-6">
            <h2 className="text-2xl font-bold text-white">Platform Performance</h2>
            <div className="ml-3">
              <VCTooltip 
                title="Live Platform Metrics"
                content="Real-time platform statistics demonstrate strong traction and market validation. High investor counts and total funding indicate product-market fit, while consistent monthly returns show the sustainable revenue model that VCs seek."
              />
            </div>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <div className="glass-card text-center p-6">
              <div className="text-3xl font-bold text-white mb-2">{dros.length}</div>
              <div className="text-sm text-slate-300 font-medium">Active DROs</div>
            </div>
            <div className="glass-card text-center p-6">
              <div className="text-3xl font-bold text-accent-green mb-2">8.5%</div>
              <div className="text-sm text-slate-300 font-medium">Avg. Monthly Return</div>
            </div>
            <div className="glass-card text-center p-6">
              <div className="text-3xl font-bold text-primary-400 mb-2">$2.4M</div>
              <div className="text-sm text-slate-300 font-medium">Total Funded</div>
            </div>
            <div className="glass-card text-center p-6">
              <div className="text-3xl font-bold text-secondary-400 mb-2">1,200+</div>
              <div className="text-sm text-slate-300 font-medium">Investors</div>
            </div>
          </div>
        </div>

        {/* Filters */}
        <div className="mb-8">
          <div className="flex items-center mb-6">
            <h2 className="text-2xl font-bold text-white">Smart Investment Filters</h2>
            <div className="ml-3">
              <VCTooltip 
                title="Advanced Filtering System"
                content="Our sophisticated filtering system allows investors to quickly find opportunities matching their investment criteria. This reduces time-to-investment and improves user experience, leading to higher conversion rates and platform engagement."
              />
            </div>
          </div>
          <DROFilters onFiltersChange={handleFiltersChange} isLoading={loading} />
        </div>

        {/* Results Header */}
        <div className="flex items-center justify-between mb-8">
          <div className="text-lg text-slate-300">
            {dros.length > 0 ? (
              <>
                <span className="text-white font-semibold">{pagination.total}</span> investment opportunities
                {filters.search && (
                  <span className="ml-2 px-3 py-1 bg-primary-500/20 text-primary-300 rounded-full text-sm">
                    for "{filters.search}"
                  </span>
                )}
              </>
            ) : (
              <span className="text-slate-400">No opportunities found</span>
            )}
          </div>

          {loading && (
            <div className="flex items-center text-slate-300">
              <Loader2 className="w-5 h-5 animate-spin mr-3" />
              <span className="font-medium">Loading opportunities...</span>
            </div>
          )}
        </div>

        {/* Featured DROs Section with Progressive Loading */}
        <div className="mb-12">
          <h2 className="text-2xl font-bold text-white mb-6 flex items-center">
            ðŸ”¥ Hot Investment Opportunities
            <span className="ml-3 px-3 py-1 bg-accent-gold/20 text-accent-gold rounded-full text-sm font-medium">
              Trending
            </span>
          </h2>
          <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-8 mb-12">
            {showSkeletons ? (
              <SkeletonLoader type="card" count={3} />
            ) : dros.length > 0 ? (
              dros.filter(dro => {
                const fundingPercentage = dro.target_amount > 0 ? 
                  ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0;
                return fundingPercentage >= 50;
              }).slice(0, 3).map((dro) => (
                <DROCard
                  key={dro.id}
                  dro={dro}
                  onInvest={handleInvest}
                  onViewDetails={handleViewDetails}
                />
              ))}
            </div>
          </div>
        )}

        {/* All DROs Grid with Progressive Loading */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-white mb-6">All Investment Opportunities</h2>
          <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-8">
            {showSkeletons ? (
              <SkeletonLoader type="card" count={12} />
            ) : dros.length > 0 ? (
              dros.map((dro, index) => (
                <div
                  key={dro.id}
                  style={{
                    animationDelay: `${index * 100}ms`,
                    opacity: 0,
                    animation: 'fadeInUp 0.6s ease-out forwards'
                  }}
                >
                  <DROCard
                    dro={dro}
                    onInvest={handleInvest}
                    onViewDetails={handleViewDetails}
                  />
                </div>
              ))
            ) : !loading && (
              <div className="col-span-full text-center py-12">
                <div className="glass-card p-8">
                  <AlertCircle className="w-12 h-12 text-slate-400 mx-auto mb-4" />
                  <h3 className="text-xl font-semibold text-white mb-2">No opportunities found</h3>
                  <p className="text-slate-300">Try adjusting your filters or search criteria</p>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Pagination - only show if we have data */}
        {dros.length > 0 && totalPages > 1 && (
              <div className="flex items-center justify-center mt-12">
                <div className="glass-card px-6 py-4">
                  <div className="flex items-center space-x-4">
                    <button
                      onClick={() => handlePageChange(pagination.page - 1)}
                      disabled={pagination.page === 1}
                      className="btn-crypto-secondary flex items-center space-x-2 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <ChevronLeft className="h-4 w-4" />
                      <span>Previous</span>
                    </button>

                    <div className="flex items-center space-x-2">
                      {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                        const pageNum = Math.max(1, Math.min(totalPages - 4, pagination.page - 2)) + i;
                        if (pageNum <= totalPages) {
                          return (
                            <button
                              key={pageNum}
                              onClick={() => handlePageChange(pageNum)}
                              className={`w-10 h-10 rounded-lg font-semibold transition-all duration-200 ${
                                pageNum === pagination.page
                                  ? 'bg-gradient-primary text-white shadow-crypto'
                                  : 'text-slate-300 hover:text-white hover:bg-white/10'
                              }`}
                            >
                              {pageNum}
                            </button>
                          );
                        }
                        return null;
                      })}
                    </div>

                    <button
                      onClick={() => handlePageChange(pagination.page + 1)}
                      disabled={pagination.page === totalPages}
                      className="btn-crypto-secondary flex items-center space-x-2 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      <span>Next</span>
                      <ChevronRight className="h-4 w-4" />
                    </button>
                  </div>

                  <div className="text-center mt-3">
                    <span className="text-sm text-slate-400">
                      Page <span className="text-white font-medium">{pagination.page}</span> of <span className="text-white font-medium">{totalPages}</span>
                    </span>
                  </div>
                </div>
              </div>
            )}
        )}
                  >
                    Browse All DROs
                  </button>
                </div>
              </div>
            </div>
          )
        )}
      </div>

      {/* Mobile Floating Recommended Button */}
      <div className="lg:hidden fixed bottom-6 right-6 z-50">
        <button
          onClick={() => onNavigateToRecommended?.()}
          className="btn-crypto-primary text-lg px-6 py-4 rounded-full shadow-2xl flex items-center space-x-3 glow-primary animate-bounce"
        >
          <Target className="w-6 h-6" />
          <span>ðŸŽ¯ Get Recs</span>
        </button>
      </div>

      {/* Modals */}
      {showInvestmentModal && selectedDRO && (
        <InvestmentModal
          dro={selectedDRO}
          onClose={() => {
            setShowInvestmentModal(false);
            setSelectedDRO(null);
          }}
          onSuccess={handleInvestmentSuccess}
        />
      )}

      {showDetailModal && selectedDRO && (
        <DRODetailModal
          dro={selectedDRO}
          onClose={() => {
            setShowDetailModal(false);
            setSelectedDRO(null);
          }}
          onInvest={handleInvest}
        />
      )}
    </div>
  );
};

export default DROList;

