                    {formatCurrency(performance.net_profit)}
                  </p>
                </div>
                <BarChart3 className="w-8 h-8 text-blue-600" />
              </div>
            </div>

            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600">ROI</p>
                  <p className={`text-2xl font-bold ${performance.roi_percentage >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                    {formatPercentage(performance.roi_percentage)}
                  </p>
                </div>
                <Target className="w-8 h-8 text-purple-600" />
              </div>
            </div>
          </div>
        )}

        {/* Tabs */}
        <div className="mb-6">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8">
              {[
                { id: 'overview', label: 'Overview', icon: BarChart3 },
                { id: 'investments', label: 'My Investments', icon: Target },
                { id: 'payouts', label: 'Payouts', icon: DollarSign },
              ].map((tab) => {
                const Icon = tab.icon;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id as any)}
                    className={`flex items-center py-2 px-1 border-b-2 font-medium text-sm ${
                      activeTab === tab.id
                        ? 'border-sirex-blue text-sirex-blue'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    }`}
                  >
                    <Icon className="w-4 h-4 mr-2" />
                    {tab.label}
                  </button>
                );
              })}
            </nav>
          </div>
        </div>

        {/* Tab Content */}
        {activeTab === 'overview' && (
          <div className="space-y-6">
            {/* Quick Stats */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Active Investments</h3>
                <div className="text-3xl font-bold text-sirex-blue mb-2">
                  {performance?.active_investments || 0}
                </div>
                <p className="text-sm text-gray-600">Currently generating returns</p>
              </div>

              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Monthly Average</h3>
                <div className="text-3xl font-bold text-green-600 mb-2">
                  {formatCurrency(performance?.average_monthly_return || 0)}
                </div>
                <p className="text-sm text-gray-600">Average monthly returns</p>
              </div>

              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Pending Payouts</h3>
                <div className="text-3xl font-bold text-yellow-600 mb-2">
                  {formatCurrency(performance?.pending_payouts || 0)}
                </div>
                <p className="text-sm text-gray-600">Awaiting processing</p>
              </div>
            </div>

            {/* Recent Activity */}
            <div className="bg-white rounded-lg shadow">
