/* CSS Custom Properties for Navy Blue & Light Gray Theme */
:root {
  --primary-50: 248 250 252;
  --primary-500: 30 64 175;
  --primary-900: 15 23 42;
  --secondary-500: 71 85 105;
  --accent-gold: 245 158 11;
  --navy-900: 15 23 42;
  --navy-800: 30 41 59;
  --navy-700: 51 65 85;
  --gray-50: 248 250 252;
  --gray-100: 241 245 249;
  --gray-200: 226 232 240;
  --glass-white: rgba(255, 255, 255, 0.15);
  --glass-navy: rgba(15, 23, 42, 0.85);
}

/* Modern Base Styles */
* {
  box-sizing: border-box;
}

html {
  font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  line-height: 1.6;
  font-feature-settings: 'cv11', 'ss01';
  font-variant-numeric: tabular-nums;
  scroll-behavior: smooth;
}

body {
  margin: 0;
  min-width: 320px;
  min-height: 100vh;
  background: #f8fafc;
  color: #1e293b;
  font-weight: 400;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Dark mode option for demos */
body.dark {
  background: #0f172a;
  color: #f1f5f9;
}

/* Demo-friendly navigation */
.demo-nav {
  background: #ffffff;
  border-bottom: 1px solid #e2e8f0;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
}

.demo-nav.dark {
  background: #1e293b;
  border-bottom: 1px solid #334155;
}

/* Navy Blue & Gray Gradient Utilities */
.crypto-gradient-primary {
  background: linear-gradient(135deg, #1e3a8a 0%, #475569 100%);
}

.crypto-gradient-secondary {
  background: linear-gradient(135deg, #475569 0%, #64748b 100%);
}

.navy-gradient {
  background: linear-gradient(135deg, #0f172a 0%, #1e3a8a 100%);
}

.gray-gradient {
  background: linear-gradient(135deg, #f1f5f9 0%, #cbd5e1 100%);
}

.crypto-gradient-gold {
  background: linear-gradient(135deg, #f59e0b 0%, #f97316 100%);
}

.crypto-gradient-mesh {
  background: linear-gradient(45deg, #667eea 0%, #764ba2 25%, #f093fb 50%, #f5576c 75%, #4facfe 100%);
}

/* Text Gradients */
.text-gradient-primary {
  background: linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
}

.text-gradient-gold {
  background: linear-gradient(135deg, #f59e0b 0%, #f97316 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
}

/* Clean Card Components - Demo Optimized */
.clean-card {
  background: #ffffff;
  border: 1px solid #e2e8f0;
  border-radius: 12px;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.1);
}

.clean-card-dark {
  background: #1e293b;
  border: 1px solid #334155;
  border-radius: 12px;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.2);
}

.demo-card {
  background: #ffffff;
  border: 1px solid #cbd5e1;
  border-radius: 16px;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  transition: all 0.2s ease;
}

.demo-card:hover {
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
  transform: translateY(-2px);
}

.demo-card-dark {
  background: #1e293b;
  border: 1px solid #475569;
  color: #f1f5f9;
}

/* Legacy glass support for gradual migration */
.glass-card {
  background: #ffffff;
  border: 1px solid #e2e8f0;
  border-radius: 16px;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

/* Standardized Button Size System */
.btn-xs {
  padding: 0.375rem 0.75rem;
  font-size: 0.75rem;
  border-radius: 0.5rem;
}

.btn-sm {
  padding: 0.5rem 1rem;
  font-size: 0.875rem;
  border-radius: 0.625rem;
}

.btn-md {
  padding: 0.75rem 1.5rem;
  font-size: 0.875rem;
  border-radius: 0.75rem;
}

.btn-lg {
  padding: 1rem 2rem;
  font-size: 1rem;
  border-radius: 0.875rem;
}

.btn-xl {
  padding: 1.25rem 2.5rem;
