@tailwind base;
@tailwind components;
@tailwind utilities;

/* CSS Custom Properties for Navy Blue & Light Gray Theme */
:root {
  --primary-50: 248 250 252;
  --primary-500: 30 64 175;
  --primary-900: 15 23 42;
  --secondary-500: 71 85 105;
  --accent-gold: 245 158 11;
  --navy-900: 15 23 42;
  --navy-800: 30 41 59;
  --navy-700: 51 65 85;
  --gray-50: 248 250 252;
  --gray-100: 241 245 249;
  --gray-200: 226 232 240;
  --glass-white: rgba(255, 255, 255, 0.15);
  --glass-navy: rgba(15, 23, 42, 0.85);
}

/* Modern Base Styles */
* {
  box-sizing: border-box;
}

html {
  font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  line-height: 1.6;
  font-feature-settings: 'cv11', 'ss01';
  font-variant-numeric: tabular-nums;
  scroll-behavior: smooth;
}

body {
  margin: 0;
  min-width: 320px;
  min-height: 100vh;
  background: linear-gradient(135deg, #1e3a8a 0%, #334155 50%, #64748b 100%);
  color: #f1f5f9;
  font-weight: 400;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Light mode option for demos */
body.light {
  background: #f8fafc;
  color: #1e293b;
}

/* Demo-friendly navigation */
.demo-nav {
  background: #ffffff;
  border-bottom: 1px solid #e2e8f0;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
}

.demo-nav.dark {
  background: #1e293b;
  border-bottom: 1px solid #334155;
}

/* Navy Blue & Gray Gradient Utilities */
.crypto-gradient-primary {
  background: linear-gradient(135deg, #1e3a8a 0%, #475569 100%);
}

.crypto-gradient-secondary {
  background: linear-gradient(135deg, #475569 0%, #64748b 100%);
}

.navy-gradient {
  background: linear-gradient(135deg, #0f172a 0%, #1e3a8a 100%);
}

.gray-gradient {
  background: linear-gradient(135deg, #f1f5f9 0%, #cbd5e1 100%);
}

.crypto-gradient-gold {
  background: linear-gradient(135deg, #f59e0b 0%, #f97316 100%);
}

.crypto-gradient-mesh {
  background: linear-gradient(45deg, #667eea 0%, #764ba2 25%, #f093fb 50%, #f5576c 75%, #4facfe 100%);
}

/* Text Gradients */
.text-gradient-primary {
  background: linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
}

.text-gradient-gold {
  background: linear-gradient(135deg, #f59e0b 0%, #f97316 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  color: transparent;
}

/* Clean Card Components - Demo Optimized */
.clean-card {
  background: #ffffff;
  border: 1px solid #e2e8f0;
  border-radius: 12px;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.1);
}

.clean-card-dark {
  background: #1e293b;
  border: 1px solid #334155;
  border-radius: 12px;
  box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.2);
}

.demo-card {
  background: #ffffff;
  border: 1px solid #cbd5e1;
  border-radius: 16px;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
  transition: all 0.2s ease;
}

.demo-card:hover {
  box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
  transform: translateY(-2px);
}

.demo-card-dark {
  background: #1e293b;
  border: 1px solid #475569;
  color: #f1f5f9;
}

/* Legacy glass support for gradual migration */
.glass-card {
  background: #ffffff;
  border: 1px solid #e2e8f0;
  border-radius: 16px;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

/* Standardized Button Size System */
.btn-xs {
  padding: 0.375rem 0.75rem;
  font-size: 0.75rem;
  border-radius: 0.5rem;
}

.btn-sm {
  padding: 0.5rem 1rem;
  font-size: 0.875rem;
  border-radius: 0.625rem;
}

.btn-md {
  padding: 0.75rem 1.5rem;
  font-size: 0.875rem;
  border-radius: 0.75rem;
}

.btn-lg {
  padding: 1rem 2rem;
  font-size: 1rem;
  border-radius: 0.875rem;
}

.btn-xl {
  padding: 1.25rem 2.5rem;
  font-size: 1.125rem;
  border-radius: 1rem;
}

/* Demo-Optimized Button Styles with Stable Contrast */
.btn-primary {
  background: #1e40af !important;
  color: #ffffff !important;
  font-weight: 600;
  border: none;
  cursor: pointer;
  transition: all 0.2s ease;
  box-shadow: 0 2px 4px 0 rgba(30, 64, 175, 0.2);
}

.btn-primary:hover {
  background: #1d4ed8 !important;
  color: #ffffff !important;
  box-shadow: 0 4px 6px 0 rgba(30, 64, 175, 0.3);
  transform: translateY(-1px);
}

.btn-secondary {
  background: #64748b !important;
  color: #ffffff !important;
  font-weight: 600;
  border: none;
  cursor: pointer;
  transition: all 0.2s ease;
}

.btn-secondary:hover {
  background: #475569 !important;
  color: #ffffff !important;
  transform: translateY(-1px);
}

/* Legacy crypto button support with stable contrast */
.btn-crypto-primary {
  background: #1e40af !important;
  color: #ffffff !important;
  font-weight: 600;
  border: none;
  cursor: pointer;
  transition: all 0.2s ease;
  box-shadow: 0 2px 4px 0 rgba(30, 64, 175, 0.2);
}

.btn-crypto-primary:hover {
  background: #1d4ed8 !important;
  color: #ffffff !important;
  box-shadow: 0 4px 6px 0 rgba(30, 64, 175, 0.3);
  transform: translateY(-1px);
}

.btn-crypto-primary:active {
  transform: translateY(0);
}

.btn-crypto-secondary {
  background: rgba(255, 255, 255, 0.15) !important;
  color: #ffffff !important;
  font-weight: 600;
  border: 1px solid rgba(255, 255, 255, 0.4) !important;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
}

.btn-crypto-secondary:hover {
  background: rgba(255, 255, 255, 0.25) !important;
  border-color: rgba(255, 255, 255, 0.6) !important;
  transform: translateY(-1px);
  box-shadow: 0 4px 6px 0 rgba(0, 0, 0, 0.15);
  color: #ffffff !important;
}

.btn-crypto-gold {
  background: linear-gradient(135deg, #f59e0b 0%, #f97316 100%) !important;
  color: #ffffff !important;
  font-weight: 600;
  border: none;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 0 4px 15px 0 rgba(245, 158, 11, 0.3);
}

.btn-crypto-gold:hover {
  background: linear-gradient(135deg, #f97316 0%, #ea580c 100%) !important;
  color: #ffffff !important;
  transform: translateY(-2px);
  box-shadow: 0 8px 25px 0 rgba(245, 158, 11, 0.4);
}

/* Card Hover Effects */
.card-hover {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
}

.card-hover:hover {
  transform: translateY(-8px);
  box-shadow: 
    0 20px 25px -5px rgba(0, 0, 0, 0.1), 
    0 10px 10px -5px rgba(0, 0, 0, 0.04),
    0 0 20px rgba(59, 130, 246, 0.3);
}

/* Glow Effects */
.glow-primary {
  box-shadow: 0 0 20px rgba(59, 130, 246, 0.3);
}

.glow-primary:hover {
  box-shadow: 0 0 30px rgba(59, 130, 246, 0.5);
}

.glow-gold {
  box-shadow: 0 0 20px rgba(245, 158, 11, 0.3);
}

.glow-gold:hover {
  box-shadow: 0 0 30px rgba(245, 158, 11, 0.5);
}

/* Shimmer Effect */
.shimmer {
  position: relative;
  overflow: hidden;
}

.shimmer::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(255, 255, 255, 0.1),
    transparent
  );
  transition: left 0.5s;
}

.shimmer:hover::before {
  left: 100%;
}

/* Status Badges */
.badge-active {
  background: linear-gradient(135deg, #10b981 0%, #059669 100%);
  color: white;
  padding: 0.25rem 0.75rem;
  border-radius: 9999px;
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.badge-pending {
  background: linear-gradient(135deg, #f59e0b 0%, #d97706 100%);
  color: white;
  padding: 0.25rem 0.75rem;
  border-radius: 9999px;
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.badge-completed {
  background: linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%);
  color: white;
  padding: 0.25rem 0.75rem;
  border-radius: 9999px;
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

/* Progress Bars */
.progress-bar {
  width: 100%;
  height: 0.5rem;
  background-color: rgba(255, 255, 255, 0.1);
  border-radius: 9999px;
  overflow: hidden;
  position: relative;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(135deg, #10b981 0%, #059669 100%);
  border-radius: 9999px;
  transition: width 0.3s ease-in-out;
  position: relative;
}

.progress-fill::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(255, 255, 255, 0.3),
    transparent
  );
  animation: shimmer 2s infinite;
}

/* Scrollbar Styling */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: rgba(255, 255, 255, 0.05);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb {
  background: linear-gradient(135deg, #3b82f6 0%, #8b5cf6 100%);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(135deg, #2563eb 0%, #7c3aed 100%);
}

/* Selection Styling */
::selection {
  background: rgba(59, 130, 246, 0.3);
  color: #e2e8f0;
}

/* Focus Styles */
.focus-ring {
  outline: 2px solid transparent;
  outline-offset: 2px;
}

.focus-ring:focus {
  outline: 2px solid #3b82f6;
  outline-offset: 2px;
}

/* Legacy Compatibility - Keep for existing components */
.sirex-gradient {
  background: linear-gradient(135deg, #1e40af 0%, #3b82f6 100%);
}

.sirex-text-gradient {
  background: linear-gradient(135deg, #1e40af 0%, #3b82f6 100%);
