import React, { useState, useEffect } from 'react';
import type { Investment } from '../services/api';
import { droAPI, revenueAPI } from '../services/api';
import { 
  TrendingUp, 
  DollarSign, 
  Target, 
  Calendar,
  BarChart3,
  Download,
  RefreshCw,
  AlertCircle,
  Eye
} from 'lucide-react';

interface PortfolioSummary {
  total_invested: number;
  active_investments: number;
  total_return_earned: number;
  investments: Investment[];
}

interface PayoutSummary {
  total_payouts: number;
  total_amount: number;
  pending_amount: number;
  completed_amount: number;
}

interface Performance {
  total_invested: number;
  total_returns: number;
  net_profit: number;
  roi_percentage: number;
  active_investments: number;
  completed_payouts: number;
  pending_payouts: number;
  average_monthly_return: number;
}

const InvestorDashboard: React.FC = () => {
  const [portfolio, setPortfolio] = useState<PortfolioSummary | null>(null);
  const [payouts, setPayouts] = useState<any[]>([]);
  const [payoutSummary, setPayoutSummary] = useState<PayoutSummary | null>(null);
  const [performance, setPerformance] = useState<Performance | null>(null);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'overview' | 'investments' | 'payouts'>('overview');

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatPercentage = (value: number) => {
    return `${value >= 0 ? '+' : ''}${value.toFixed(2)}%`;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const fetchDashboardData = async () => {
    try {
      setError(null);

      // Progressive loading: Load critical data first, then secondary data
      try {
        const portfolioResponse = await droAPI.getMyInvestments();
        setPortfolio(portfolioResponse);
        setLoading(false); // Show portfolio immediately
      } catch (err) {
        console.error('Portfolio fetch error:', err);
      }

      // Load secondary data in background
      try {
        const [payoutsResponse, performanceResponse] = await Promise.all([
          revenueAPI.getMyPayouts(1, 10),
          revenueAPI.getPerformance(),
        ]);

        setPayouts(payoutsResponse.payouts);
        setPayoutSummary(payoutsResponse.summary);
        setPerformance(performanceResponse.performance);
      } catch (err) {
        console.error('Secondary data fetch error:', err);
        // Don't set error for secondary data failures
      }
    } catch (err) {
      console.error('Dashboard data fetch error:', err);
      setError('Failed to load dashboard data. Please try again.');
      setLoading(false);
    } finally {
      setRefreshing(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const handleRefresh = async () => {
    setRefreshing(true);
    await fetchDashboardData();
  };

  const getInvestmentStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'completed': return 'bg-blue-100 text-blue-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getPayoutStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'failed': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-sirex-blue mx-auto mb-4"></div>
          <p className="text-gray-600 text-lg">Loading your dashboard...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto">
          <AlertCircle className="w-12 h-12 text-red-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Error Loading Dashboard</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button onClick={fetchDashboardData} className="btn-primary">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-dark">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-white">Investor Dashboard</h1>
            <p className="text-slate-300">Track your investments and returns</p>
          </div>
          <button
            onClick={handleRefresh}
            disabled={refreshing}
            className="flex items-center btn-secondary"
          >
            <RefreshCw className={`w-4 h-4 mr-2 ${refreshing ? 'animate-spin' : ''}`} />
            Refresh
          </button>
        </div>

        {/* Performance Metrics */}
        {performance && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600">Total Invested</p>
                  <p className="text-2xl font-bold text-gray-900">
                    {formatCurrency(performance.total_invested)}
                  </p>
                </div>
                <DollarSign className="w-8 h-8 text-sirex-blue" />
              </div>
            </div>

            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600">Total Returns</p>
                  <p className="text-2xl font-bold text-green-600">
                    {formatCurrency(performance.total_returns)}
                  </p>
                </div>
                <TrendingUp className="w-8 h-8 text-green-600" />
              </div>
            </div>

            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600">Net Profit</p>
                  <p className={`text-2xl font-bold ${performance.net_profit >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                    {formatCurrency(performance.net_profit)}
                  </p>
                </div>
                <BarChart3 className="w-8 h-8 text-blue-600" />
              </div>
            </div>

            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600">ROI</p>
                  <p className={`text-2xl font-bold ${performance.roi_percentage >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                    {formatPercentage(performance.roi_percentage)}
                  </p>
                </div>
                <Target className="w-8 h-8 text-purple-600" />
              </div>
            </div>
          </div>
        )}

        {/* Tabs */}
        <div className="mb-6">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8">
              {[
                { id: 'overview', label: 'Overview', icon: BarChart3 },
                { id: 'investments', label: 'My Investments', icon: Target },
                { id: 'payouts', label: 'Payouts', icon: DollarSign },
              ].map((tab) => {
                const Icon = tab.icon;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id as any)}
                    className={`flex items-center py-2 px-1 border-b-2 font-medium text-sm ${
                      activeTab === tab.id
                        ? 'border-sirex-blue text-sirex-blue'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    }`}
                  >
                    <Icon className="w-4 h-4 mr-2" />
                    {tab.label}
                  </button>
                );
              })}
            </nav>
          </div>
        </div>

        {/* Tab Content */}
        {activeTab === 'overview' && (
          <div className="space-y-6">
            {/* Quick Stats */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Active Investments</h3>
                <div className="text-3xl font-bold text-sirex-blue mb-2">
                  {performance?.active_investments || 0}
                </div>
                <p className="text-sm text-gray-600">Currently generating returns</p>
              </div>

              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Monthly Average</h3>
                <div className="text-3xl font-bold text-green-600 mb-2">
                  {formatCurrency(performance?.average_monthly_return || 0)}
                </div>
                <p className="text-sm text-gray-600">Average monthly returns</p>
              </div>

              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Pending Payouts</h3>
                <div className="text-3xl font-bold text-yellow-600 mb-2">
                  {formatCurrency(performance?.pending_payouts || 0)}
                </div>
                <p className="text-sm text-gray-600">Awaiting processing</p>
              </div>
            </div>

            {/* Recent Activity */}
            <div className="bg-white rounded-lg shadow">
              <div className="p-6 border-b border-gray-200">
                <h3 className="text-lg font-semibold text-gray-900">Recent Activity</h3>
              </div>
              <div className="p-6">
                {payouts.length > 0 ? (
                  <div className="space-y-4">
                    {payouts.slice(0, 5).map((payout, index) => (
                      <div key={index} className="flex items-center justify-between">
                        <div className="flex items-center">
                          <div className="w-2 h-2 bg-sirex-blue rounded-full mr-3"></div>
                          <div>
                            <p className="font-medium text-gray-900">
                              Payout from {payout.dro_title || 'Unknown DRO'}
                            </p>
                            <p className="text-sm text-gray-500">
                              {formatDate(payout.payout_date)}
                            </p>
                          </div>
                        </div>
                        <div className="text-right">
                          <p className="font-medium text-green-600">
                            {formatCurrency(payout.payout_amount)}
                          </p>
                          <span className={`inline-block px-2 py-1 text-xs rounded-full ${getPayoutStatusColor(payout.status)}`}>
                            {payout.status}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8 text-gray-500">
                    <Calendar className="w-12 h-12 mx-auto mb-2 text-gray-300" />
                    <p>No recent activity</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}

        {activeTab === 'investments' && portfolio && (
          <div className="bg-white rounded-lg shadow">
            <div className="p-6 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">My Investments</h3>
                <button className="flex items-center btn-secondary text-sm">
                  <Download className="w-4 h-4 mr-2" />
                  Export
                </button>
              </div>
            </div>
            <div className="overflow-x-auto">
              {portfolio.investments.length > 0 ? (
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        DRO
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Amount
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Date
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Status
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Returns
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {portfolio.investments.map((investment) => (
                      <tr key={investment.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div>
                            <div className="text-sm font-medium text-gray-900">
                              {investment.dro_title || 'Unknown DRO'}
                            </div>
                            <div className="text-sm text-gray-500">
                              {investment.dro_industry} â€¢ {investment.dro_revenue_percentage}% share
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">
                            {formatCurrency(investment.amount)}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {formatDate(investment.investment_date)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getInvestmentStatusColor(investment.status)}`}>
                            {investment.status}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {formatCurrency(0)} {/* Would show actual returns */}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                          <button className="text-sirex-blue hover:text-sirex-light-blue flex items-center">
                            <Eye className="w-4 h-4 mr-1" />
                            View
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <div className="text-center py-16">
                  <Target className="w-16 h-16 mx-auto mb-4 text-gray-300" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No Investments Yet</h3>
                  <p className="text-gray-600 mb-6">Start investing in DROs to see them here.</p>
                  <button className="btn-primary">Browse Opportunities</button>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === 'payouts' && (
          <div className="bg-white rounded-lg shadow">
            <div className="p-6 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">Payout History</h3>
                <div className="flex items-center space-x-4">
                  {payoutSummary && (
                    <div className="text-sm text-gray-600">
                      Total: {formatCurrency(payoutSummary.total_amount)}
                    </div>
                  )}
                  <button className="flex items-center btn-secondary text-sm">
                    <Download className="w-4 h-4 mr-2" />
                    Export
                  </button>
                </div>
              </div>
            </div>
            <div className="overflow-x-auto">
              {payouts.length > 0 ? (
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        DRO
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Amount
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Date
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Status
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Investment
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {payouts.map((payout, index) => (
                      <tr key={index} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">
                            {payout.dro_title || 'Unknown DRO'}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-green-600">
                            {formatCurrency(payout.payout_amount)}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {formatDate(payout.payout_date)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getPayoutStatusColor(payout.status)}`}>
                            {payout.status}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {formatCurrency(payout.investment_amount || 0)}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <div className="text-center py-16">
                  <DollarSign className="w-16 h-16 mx-auto mb-4 text-gray-300" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No Payouts Yet</h3>
                  <p className="text-gray-600">Payouts will appear here once DROs start generating revenue.</p>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default InvestorDashboard;

import React, { useState, useEffect } from 'react';
import type { DRO } from '../services/api';
import { droAPI } from '../services/api';
import { 
  Plus,
  TrendingUp, 
  DollarSign, 
  Target, 
  Users,
  BarChart3,
  Calendar,
  Edit,
  Eye,
  AlertCircle,
  RefreshCw,
  Download
} from 'lucide-react';

interface MSMEStats {
  total_dros: number;
  active_dros: number;
  total_funding: number;
  completed_dros: number;
}

const MSMEDashboard: React.FC = () => {
  const [dros, setDROs] = useState<DRO[]>([]);
  const [, setStats] = useState<MSMEStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'overview' | 'dros' | 'revenue'>('overview');

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const fetchDashboardData = async () => {
    try {
      setError(null);

      const [droResponse, statsResponse] = await Promise.all([
        droAPI.getMyDROs(),
        droAPI.getStatistics(),
      ]);

      setDROs(droResponse.dros);
      setStats(statsResponse.statistics);
    } catch (err) {
      console.error('Dashboard data fetch error:', err);
      setError('Failed to load dashboard data. Please try again.');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const handleRefresh = async () => {
    setRefreshing(true);
    await fetchDashboardData();
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'completed': return 'bg-blue-100 text-blue-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getFundingPercentage = (dro: DRO) => {
    return dro.target_amount > 0 ? ((dro.raised_amount || 0) / dro.target_amount) * 100 : 0;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-sirex-blue mx-auto mb-4"></div>
          <p className="text-gray-600 text-lg">Loading your dashboard...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto">
          <AlertCircle className="w-12 h-12 text-red-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Error Loading Dashboard</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button onClick={fetchDashboardData} className="btn-primary">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  const totalRaised = dros.reduce((sum, dro) => sum + (dro.raised_amount || 0), 0);
  const totalTarget = dros.reduce((sum, dro) => sum + dro.target_amount, 0);
  const totalInvestors = dros.reduce((sum, dro) => sum + (dro.investor_count || 0), 0);

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">MSME Dashboard</h1>
            <p className="text-gray-600">Manage your DROs and track funding</p>
          </div>
          <div className="flex items-center space-x-4">
            <button
              onClick={handleRefresh}
              disabled={refreshing}
              className="flex items-center btn-secondary"
            >
              <RefreshCw className={`w-4 h-4 mr-2 ${refreshing ? 'animate-spin' : ''}`} />
              Refresh
            </button>
            <button className="flex items-center btn-primary">
              <Plus className="w-4 h-4 mr-2" />
              Create DRO
            </button>
          </div>
        </div>

        {/* Metrics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total DROs</p>
                <p className="text-2xl font-bold text-gray-900">{dros.length}</p>
              </div>
              <Target className="w-8 h-8 text-sirex-blue" />
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Raised</p>
                <p className="text-2xl font-bold text-green-600">
                  {formatCurrency(totalRaised)}
                </p>
              </div>
              <DollarSign className="w-8 h-8 text-green-600" />
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Active DROs</p>
                <p className="text-2xl font-bold text-blue-600">
                  {dros.filter(dro => dro.status === 'active').length}
                </p>
              </div>
              <TrendingUp className="w-8 h-8 text-blue-600" />
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Investors</p>
                <p className="text-2xl font-bold text-purple-600">{totalInvestors}</p>
              </div>
              <Users className="w-8 h-8 text-purple-600" />
            </div>
          </div>
        </div>

        {/* Funding Progress Overview */}
        {totalTarget > 0 && (
          <div className="bg-white rounded-lg shadow p-6 mb-8">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Overall Funding Progress</h3>
            <div className="mb-4">
              <div className="flex items-center justify-between text-sm mb-2">
                <span className="text-gray-600">Total Progress</span>
                <span className="font-medium">{((totalRaised / totalTarget) * 100).toFixed(1)}%</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-3">
                <div 
                  className="sirex-gradient h-3 rounded-full transition-all duration-300"
                  style={{ width: `${Math.min((totalRaised / totalTarget) * 100, 100)}%` }}
                ></div>
              </div>
              <div className="flex items-center justify-between text-sm text-gray-500 mt-1">
                <span>{formatCurrency(totalRaised)} raised</span>
                <span>{formatCurrency(totalTarget)} target</span>
              </div>
            </div>
          </div>
        )}

        {/* Tabs */}
        <div className="mb-6">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8">
              {[
                { id: 'overview', label: 'Overview', icon: BarChart3 },
                { id: 'dros', label: 'My DROs', icon: Target },
                { id: 'revenue', label: 'Revenue Tracking', icon: TrendingUp },
              ].map((tab) => {
                const Icon = tab.icon;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id as any)}
                    className={`flex items-center py-2 px-1 border-b-2 font-medium text-sm ${
                      activeTab === tab.id
                        ? 'border-sirex-blue text-sirex-blue'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    }`}
                  >
                    <Icon className="w-4 h-4 mr-2" />
                    {tab.label}
                  </button>
                );
              })}
            </nav>
          </div>
        </div>

        {/* Tab Content */}
        {activeTab === 'overview' && (
          <div className="space-y-6">
            {/* Recent Activity */}
            <div className="bg-white rounded-lg shadow">
              <div className="p-6 border-b border-gray-200">
                <h3 className="text-lg font-semibold text-gray-900">Recent Activity</h3>
              </div>
              <div className="p-6">
                {dros.length > 0 ? (
                  <div className="space-y-4">
                    {dros.slice(0, 5).map((dro) => (
                      <div key={dro.id} className="flex items-center justify-between">
                        <div className="flex items-center">
                          <div className="w-2 h-2 bg-sirex-blue rounded-full mr-3"></div>
                          <div>
                            <p className="font-medium text-gray-900">{dro.title}</p>
                            <p className="text-sm text-gray-500">
                              {formatCurrency(dro.raised_amount || 0)} raised â€¢ {dro.investor_count || 0} investors
                            </p>
                          </div>
                        </div>
                        <div className="text-right">
                          <span className={`inline-block px-2 py-1 text-xs rounded-full ${getStatusColor(dro.status)}`}>
                            {dro.status}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8 text-gray-500">
                    <Calendar className="w-12 h-12 mx-auto mb-2 text-gray-300" />
                    <p>No DROs created yet</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}

        {activeTab === 'dros' && (
          <div className="bg-white rounded-lg shadow">
            <div className="p-6 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">My DROs</h3>
                <button className="flex items-center btn-secondary text-sm">
                  <Download className="w-4 h-4 mr-2" />
                  Export
                </button>
              </div>
            </div>
            <div className="overflow-x-auto">
              {dros.length > 0 ? (
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        DRO Details
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Funding
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Investors
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Status
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Created
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {dros.map((dro) => (
                      <tr key={dro.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div>
                            <div className="text-sm font-medium text-gray-900">{dro.title}</div>
                            <div className="text-sm text-gray-500">
                              {dro.industry} â€¢ {dro.revenue_percentage}% revenue share
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div>
                            <div className="text-sm font-medium text-gray-900">
                              {formatCurrency(dro.raised_amount || 0)} / {formatCurrency(dro.target_amount)}
                            </div>
                            <div className="w-full bg-gray-200 rounded-full h-2 mt-1">
                              <div 
                                className="sirex-gradient h-2 rounded-full"
                                style={{ width: `${Math.min(getFundingPercentage(dro), 100)}%` }}
                              ></div>
                            </div>
                            <div className="text-xs text-gray-500 mt-1">
                              {getFundingPercentage(dro).toFixed(1)}% funded
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">
                            {dro.investor_count || 0}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusColor(dro.status)}`}>
                            {dro.status}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {formatDate(dro.created_at)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                          <div className="flex items-center space-x-2">
                            <button className="text-sirex-blue hover:text-sirex-light-blue">
                              <Eye className="w-4 h-4" />
                            </button>
                            <button className="text-gray-400 hover:text-gray-600">
                              <Edit className="w-4 h-4" />
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <div className="text-center py-16">
                  <Target className="w-16 h-16 mx-auto mb-4 text-gray-300" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No DROs Created Yet</h3>
                  <p className="text-gray-600 mb-6">
                    Create your first Daily Revenue Obligation to start raising funds.
                  </p>
                  <button className="btn-primary">
                    <Plus className="w-4 h-4 mr-2" />
                    Create Your First DRO
                  </button>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === 'revenue' && (
          <div className="bg-white rounded-lg shadow">
            <div className="p-6 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">Revenue Tracking</h3>
                <button className="flex items-center btn-primary text-sm">
                  <Plus className="w-4 h-4 mr-2" />
                  Report Revenue
                </button>
              </div>
            </div>
            <div className="p-6">
              <div className="text-center py-16">
                <TrendingUp className="w-16 h-16 mx-auto mb-4 text-gray-300" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">Revenue Tracking Coming Soon</h3>
                <p className="text-gray-600">
                  Report your daily revenue to automatically distribute payments to investors.
                </p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MSMEDashboard;

import React from 'react';
import { TrendingUp, Globe, Building, DollarSign } from 'lucide-react';
import VCTooltip from './VCTooltip';

const MarketIndicators: React.FC = () => {
  const marketData = [
    {
      icon: Globe,
      value: "$1.2T",
      label: "Global SME Market",
      description: "Total addressable market for SME financing across APAC region",
      color: "text-blue-400"
    },
    {
      icon: Building,
      value: "65M",
      label: "MSMEs in APAC",
      description: "Micro, Small & Medium Enterprises seeking alternative funding solutions",
      color: "text-green-400"
    },
    {
      icon: DollarSign,
      value: "$540B",
      label: "Funding Gap",
      description: "Unmet financing demand from traditional banks and venture capital",
      color: "text-yellow-400"
    },
    {
      icon: TrendingUp,
      value: "23%",
      label: "Annual Growth",
      description: "Revenue-based financing market growth rate (2023-2028 projection)",
      color: "text-purple-400"
    }
  ];

  return (
    <div className="glass-card p-6 mb-8">
      <div className="flex items-center mb-6">
        <h3 className="text-xl font-bold text-white">Market Opportunity</h3>
        <div className="ml-3">
          <VCTooltip 
            title="Total Addressable Market"
            content="SIREX addresses the massive gap in SME financing across Asia-Pacific. With traditional banks serving <30% of SME funding needs, revenue-based financing represents a $540B opportunity growing at 23% annually."
          />
        </div>
      </div>

      <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
        {marketData.map((item, index) => {
          const Icon = item.icon;
          return (
            <div 
              key={index} 
              className="text-center p-4 rounded-lg bg-white/5 border border-white/10 transition-all duration-300 hover:bg-white/10 hover:border-white/20 hover:scale-105 cursor-pointer"
              style={{
                animationDelay: `${index * 200}ms`,
                opacity: 0,
                animation: 'scaleIn 0.6s ease-out forwards'
              }}
            >
              <div className="flex items-center justify-center mb-3">
                <div className={`p-3 rounded-full bg-white/10`}>
                  <Icon className={`w-6 h-6 ${item.color}`} />
                </div>
              </div>
              <div className={`text-2xl font-bold mb-1 ${item.color}`}>
                {item.value}
              </div>
              <div className="text-sm font-medium text-white mb-2">
                {item.label}
              </div>
              <div className="text-xs text-slate-300 leading-tight">
                {item.description}
              </div>
            </div>
          );
        })}
      </div>

      <div 
        className="mt-6 p-4 bg-gradient-to-r from-blue-500/10 to-purple-500/10 rounded-lg border border-blue-500/20 transition-all duration-300 hover:from-blue-500/20 hover:to-purple-500/20 hover:border-blue-500/30"
        style={{
          animationDelay: '800ms',
          opacity: 0,
          animation: 'fadeInUp 0.6s ease-out forwards'
        }}
      >
        <div className="flex items-center space-x-3">
          <div className="text-2xl">ðŸš€</div>
          <div>
            <div className="text-white font-semibold">SIREX Competitive Advantage</div>
            <div className="text-sm text-slate-300">
              First-mover advantage in APAC RBF market with proprietary risk assessment and automated revenue tracking
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MarketIndicators;

