      prompt: 'è«‹è¼¸å…¥æ­£æ•´æ•¸',
      validateInput: value => {
        const num = Number(value);
        return isNaN(num) || num <= 0 || num > 86400 ? 'è«‹è¼¸å…¥ 1-86400 ä¹‹é–“çš„æ­£æ•´æ•¸' : null;
      },
