  });

  // æ¸…é™¤æ­·å²è¨˜éŒ„å‘½ä»¤
  const clearHistoryDisposable = vscode.commands.registerCommand(
    'countdown.clearHistory',
    async () => {
      const choice = await vscode.window.showWarningMessage(
        'ç¢ºå®šè¦æ¸…é™¤æ‰€æœ‰è¨ˆæ™‚æ­·å²è¨˜éŒ„å—ï¼Ÿ',
