const path = require('path');
const { runTests } = require('@vscode/test-electron');

async function main() {
    try {
        // Launch VSCode with extension loaded but no automated tests
        const extensionDevelopmentPath = path.resolve(__dirname, './');

        // Download VS Code, unzip it and launch for manual testing
        await runTests({ 
            extensionDevelopmentPath,
            // No extensionTestsPath = no automated tests run
            launchArgs: [
                `--user-data-dir=${path.resolve(__dirname, './.vscode-test/user-data')}`,
                `--extensions-dir=${path.resolve(__dirname, './.vscode-test/extensions')}`,
                '--disable-extensions', // Disable all other extensions
                '--new-window'
            ]
        });
    } catch (err) {
        console.error('Failed to launch manual test environment:', err);
        process.exit(1);
    }
}

main();

