const { spawn } = require('child_process');

async function main() {
    try {
        console.log('Setting up manual test environment...');

        // Download VSCode if not already present
        const vscodeExecutablePath = await downloadAndUnzipVSCode('1.101.2');
        const [cli, ...args] = resolveCliArgsFromVSCodeExecutablePath(vscodeExecutablePath);

        const extensionDevelopmentPath = path.resolve(__dirname, './');

        // Launch VSCode with extension loaded for manual testing
        const launchArgs = [
            ...args,
            `--extensionDevelopmentPath=${extensionDevelopmentPath}`,
            `--user-data-dir=${path.resolve(__dirname, './.vscode-test/user-data')}`,
