            '--disable-extensions', // Disable all other extensions except ours
            '--new-window'
        ];

        console.log('Launching VSCode for manual testing...');
        console.log('Your countdown extension will be loaded and ready to test!');
        console.log('Look for the â° icon in the status bar.');

        const child = spawn(cli, launchArgs, {
            stdio: 'inherit'
        });

        child.on('close', (code) => {
            console.log(`VSCode closed with code ${code}`);
        });

    } catch (err) {
        console.error('Failed to launch manual test environment:', err);
        process.exit(1);
    }
}

main();

